import { useCurrency } from '@/hooks/use-currency';
import CurrencySelector from '@/components/ui/currency-selector';

interface UpgradeModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function UpgradeModal({ isOpen, onClose }: UpgradeModalProps) {
  const { user, profile } = useAuth();
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();
  const { formatPrice, convertPrice, selectedCurrency } = useCurrency();

  const [showPayment, setShowPayment] = useState(false);

  const handleUpgrade = async () => {
    if (!user) return;
    setShowPayment(true);
  };

  const handleSubscriptionSuccess = async (subscriptionId: string) => {
    try {
      console.log('Subscription successful:', { subscriptionId });

      // Invalidate profile query to refresh user data
      await queryClient.invalidateQueries({ queryKey: ['/api/profile'] });

      // Force a small delay to ensure backend has updated the profile
      await new Promise(resolve => setTimeout(resolve, 500));

      toast({
        title: "Subscription Activated!",
        description: "You've been upgraded to Pro! All features are now unlocked.",
      });

      onClose();

      // Reload the page to ensure all UI updates
      window.location.reload();
    } catch (error) {
      console.error('Profile refresh failed:', error);
      toast({
        title: "Upgrade successful",
        description: "Your subscription was activated successfully. Please refresh the page to see your new features.",
      });
      onClose();
    }
  };

  const handlePaymentError = (error: any) => {
    console.error('Payment error:', error);
    toast({
      title: "Payment failed",
      description: "There was an error processing your payment. Please try again.",
      variant: "destructive",
