      return;
    }

    const owner = await storage.getProfile(page.ownerId);
    if (!owner || !owner.email) {
      console.warn("Owner email not found for appointment:", appointmentId);
      return;
    }

    // Use bookinggen.xyz as the base URL
    const baseUrl = 'https://www.bookinggen.xyz';

    // Use the new business notification template for rescheduled appointments
    const emailData: EmailTemplateData = {
      businessName: page.title,
      customerName: appointment.customerName,
      serviceName: service?.name || 'Service',
      date: appointment.date,
      time: appointment.time,
      status: 'rescheduled',
      appointmentId: appointment.id,
      contactEmail: appointment.customerEmail || undefined,
      contactPhone: appointment.customerPhone || undefined,
      originalDate,
