      const page = await storage.getPage(appointment.pageId);
      const service = appointment.serviceId ? await storage.getServiceById(appointment.serviceId) : null;

      // Return only necessary public info
      res.json({
        id: appointment.id,
        customerName: appointment.customerName,
        customerEmail: appointment.customerEmail,
        date: appointment.date,
        time: appointment.time,
        status: appointment.status,
        serviceName: service?.name || 'Service',
        businessName: page?.title || 'Business',
        contactEmail: page?.contactEmail,
        contactPhone: page?.contactPhone,
      });
    } catch (error) {
      console.error("Get public appointment error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Public reschedule endpoint (no authentication required)
  app.post("/api/public/appointments/:id/reschedule", async (req, res) => {
    try {
      const appointment = await storage.getAppointmentById(req.params.id);

      if (!appointment) {
        return res.status(404).json({ message: "Appointment not found" });
      }

      const { newDate, newTime, note } = req.body;

      if (!newDate || !newTime) {
        return res.status(400).json({ message: "New date and time are required" });
      }

      const originalDate = appointment.date;
      const originalTime = appointment.time;

      // Update the appointment
      const updated = await storage.updateAppointment(req.params.id, {
        date: newDate,
        time: newTime,
        status: 'rescheduled'
      });

      // Send email notification to customer
      if (appointment.customerEmail) {
        await sendAppointmentEmail(req.params.id, 'rescheduled', {
          note,
          originalDate,
          originalTime
        });
      }

      // Send notification to business owner about reschedule
      await sendOwnerRescheduleNotification(req.params.id, originalDate, originalTime);

      res.json({
        success: true,
        message: "Appointment rescheduled successfully",
        appointment: updated
      });
    } catch (error) {
      console.error("Public reschedule error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Dashboard statistics route
  app.get("/api/dashboard/stats", verifyToken, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Get all pages for this user
      const pages = await storage.getPagesByOwner(userId);
      const pagesCount = Math.max(0, pages?.length || 0);

      // Use Supabase to get appointments with service prices
      if (!supabase) {
        throw new Error("Supabase not available");
      }

      const { data: appointments, error } = await supabase
        .from('appointments')
        .select(`
          *,
          services(price)
        `)
        .eq('owner_id', userId);

      if (error) {
        console.error("Error fetching appointments for stats:", error);
        throw error;
      }

      const validAppointments = appointments || [];
      const totalAppointments = Math.max(0, validAppointments.length);

      // Count pending appointments
      const pendingAppointments = Math.max(0, validAppointments.filter((apt: any) => apt.status === 'pending').length);

      // Calculate total revenue (from accepted appointments with service prices)
      const acceptedAppointments = validAppointments.filter((apt: any) => apt.status === 'accepted');
      const totalRevenue = acceptedAppointments.reduce((sum: number, apt: any) => {
        const servicePrice = apt.services?.price || 0;
        return sum + Number(servicePrice);
      }, 0);

      // Calculate conversion rate (safe division)
      const conversionRate = totalAppointments > 0
        ? Math.round((acceptedAppointments.length / totalAppointments * 100) * 10) / 10
        : 0;

      // Average booking value (safe division)
      const avgBookingValue = acceptedAppointments.length > 0
        ? Math.round((totalRevenue / acceptedAppointments.length) * 100) / 100
        : 0;

      // Ensure all values are valid numbers
      const response = {
        pagesCount: Number(pagesCount) || 0,
        totalAppointments: Number(totalAppointments) || 0,
        pendingAppointments: Number(pendingAppointments) || 0,
        totalRevenue: Number(totalRevenue) || 0,
        conversionRate: Number(conversionRate) || 0,
        avgBookingValue: Number(avgBookingValue) || 0
      };

      res.json(response);
    } catch (error) {
      console.error("Get dashboard stats error:", error);
      res.status(500).json({ 
        message: "Internal server error",
        // Return safe defaults on error
        pagesCount: 0,
        totalAppointments: 0,
        pendingAppointments: 0,
        totalRevenue: 0,
        conversionRate: 0,
        avgBookingValue: 0
      });
    }
  });

  // Recent activity route
  app.get("/api/dashboard/recent-activity", verifyToken, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Get recent appointments (last 10)
      const appointments = await storage.getAppointmentsByOwner(userId);

      // Ensure we have a valid array
      const validAppointments = Array.isArray(appointments) ? appointments : [];

      // Sort by creation date (most recent first) and take last 10
      const recentAppointments = validAppointments
        .filter(apt => apt && apt.id) // Filter out invalid appointments
        .sort((a, b) => {
          // Use a fallback date if createdAt doesn't exist
          const dateA = new Date(a.createdAt || a.id || Date.now()).getTime();
          const dateB = new Date(b.createdAt || b.id || Date.now()).getTime();
          return dateB - dateA; // Most recent first
        })
        .slice(0, 10) // Limit to 10 items
        .map(apt => ({
          id: String(apt.id || ''),
          type: 'appointment',
          title: `New appointment from ${apt.customerName || 'Unknown Customer'}`,
          description: `${apt.serviceName || 'Service'} on ${apt.date || 'TBD'} at ${apt.time || 'TBD'}`,
          time: apt.createdAt || apt.id || new Date().toISOString(),
          status: apt.status || 'pending'
        }));

      res.json(recentAppointments);
    } catch (error) {
      console.error("Get recent activity error:", error);
      res.status(500).json([]); // Return empty array on error instead of error object
    }
  });

  // Analytics chart data route
  app.get("/api/dashboard/analytics-charts", verifyToken, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      // Get all appointments for this user
      const appointments = await storage.getAppointmentsByOwner(userId);
      const validAppointments = Array.isArray(appointments) ? appointments : [];

      // Get the last 30 days
      const now = new Date();
      const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);

      // Initialize daily booking data for the last 30 days
      const dailyBookings: { [key: string]: number } = {};
      const dailyRevenue: { [key: string]: number } = {};

      for (let i = 29; i >= 0; i--) {
        const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
        const dateKey = date.toISOString().split('T')[0];
        dailyBookings[dateKey] = 0;
        dailyRevenue[dateKey] = 0;
      }

      // Count bookings by creation date (last 30 days)
      validAppointments.forEach(apt => {
        if (apt.createdAt) {
          const createdDate = new Date(apt.createdAt);
          if (createdDate >= thirtyDaysAgo) {
            const dateKey = createdDate.toISOString().split('T')[0];
            if (dailyBookings[dateKey] !== undefined) {
              dailyBookings[dateKey]++;
              if (apt.status === 'accepted') {
                dailyRevenue[dateKey] += Number(apt.price) || 0;
              }
            }
          }
        }
      });

      // Format for chart
      const bookingTrend = Object.keys(dailyBookings)
        .sort()
        .map(date => ({
          date: new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
          bookings: dailyBookings[date],
          revenue: Math.round(dailyRevenue[date] * 100) / 100
        }));

      // Status distribution
      const statusCounts = {
        pending: 0,
        accepted: 0,
        declined: 0,
        rescheduled: 0
      };

      validAppointments.forEach(apt => {
        const status = apt.status || 'pending';
        if (status in statusCounts) {
          statusCounts[status as keyof typeof statusCounts]++;
        }
      });

      const statusDistribution = [
        { name: 'Pending', value: statusCounts.pending, color: '#f97316' },
        { name: 'Accepted', value: statusCounts.accepted, color: '#10b981' },
        { name: 'Declined', value: statusCounts.declined, color: '#ef4444' },
        { name: 'Rescheduled', value: statusCounts.rescheduled, color: '#3b82f6' }
      ].filter(item => item.value > 0);

      // Revenue by status
      const revenueByStatus = {
        accepted: 0,
        pending: 0,
        rescheduled: 0
      };

      validAppointments.forEach(apt => {
        const revenue = Number(apt.price) || 0;
        if (apt.status === 'accepted') {
          revenueByStatus.accepted += revenue;
        } else if (apt.status === 'pending') {
          revenueByStatus.pending += revenue;
        } else if (apt.status === 'rescheduled') {
          revenueByStatus.rescheduled += revenue;
        }
      });

      const revenueData = [
        { status: 'Accepted', revenue: Math.round(revenueByStatus.accepted * 100) / 100, color: '#10b981' },
        { status: 'Pending', revenue: Math.round(revenueByStatus.pending * 100) / 100, color: '#f97316' },
        { status: 'Rescheduled', revenue: Math.round(revenueByStatus.rescheduled * 100) / 100, color: '#3b82f6' }
      ].filter(item => item.revenue > 0);

      res.json({
        bookingTrend,
        statusDistribution,
        revenueData
      });
    } catch (error) {
      console.error("Get analytics charts error:", error);
      res.status(500).json({ 
        bookingTrend: [],
        statusDistribution: [],
        revenueData: []
      });
    }
  });


  // Razorpay payment routes (conditionally registered)
  if (process.env.RAZORPAY_KEY_ID && process.env.RAZORPAY_KEY_SECRET) {
    app.post("/api/razorpay/order", verifyToken, async (req, res) => {
      await createRazorpayOrder(req, res);
    });

    app.post("/api/razorpay/verify", verifyToken, async (req, res) => {
      await verifyRazorpayPayment(req, res);
    });

    app.post("/api/razorpay/subscriptions", verifyToken, async (req, res) => {
      await createRazorpaySubscription(req, res);
    });

    app.get("/api/razorpay/subscriptions/:subscriptionId", verifyToken, async (req, res) => {
      await getRazorpaySubscription(req, res);
    });

    app.post("/api/razorpay/cancel-subscription", verifyToken, async (req, res) => {
      const { cancelRazorpaySubscription } = await import("./razorpay");
      await cancelRazorpaySubscription(req, res);
    });

    // Get user's subscriptions
    app.get("/api/subscriptions", verifyToken, async (req, res) => {
      try {
        const authReq = req as any;
        if (!authReq.user?.userId) {
          return res.status(401).json({ error: "User not authenticated" });
        }

        const subscriptions = await storage.getSubscriptionsByUser(authReq.user.userId);
        res.json(subscriptions);
      } catch (error) {
        console.error("Failed to get user subscriptions:", error);
        res.status(500).json({ error: "Failed to get subscriptions" });
      }
    });

    // Get user's payment history
    app.get("/api/payments", verifyToken, async (req, res) => {
      try {
        const authReq = req as any;
        if (!authReq.user?.userId) {
          return res.status(401).json({ error: "User not authenticated" });
        }

        const payments = await storage.getPaymentsByUser(authReq.user.userId);
        res.json(payments);
      } catch (error) {
        console.error("Failed to get user payments:", error);
        res.status(500).json({ error: "Failed to get payment history" });
      }
    });

    // Webhook route moved to server/index.ts for proper middleware ordering
    // This ensures express.raw() runs BEFORE express.json() for signature verification

    console.log("Razorpay payment and subscription routes registered successfully");
  } else {
    console.warn("Razorpay credentials not found - Razorpay routes not registered");
  }

  // PayPal subscription routes
  if (process.env.PAYPAL_CLIENT_ID && process.env.PAYPAL_CLIENT_SECRET) {
    const { 
      createPayPalSubscription, 
      getPayPalSubscription, 
      cancelPayPalSubscription,
      checkAndActivateSubscription,
      getPayPalPlanId
    } = await import("./paypal-subscriptions");

    const { handlePayPalWebhook } = await import("./paypal-webhook");

    // Get PayPal plan ID (public endpoint to support PayPal SDK button)
    app.get("/api/paypal/plan-id", async (req, res) => {
      await getPayPalPlanId(req, res);
    });

    app.post("/api/paypal/subscription", verifyToken, async (req, res) => {
      await createPayPalSubscription(req, res);
    });

    app.get("/api/paypal/subscription/:subscriptionId", verifyToken, async (req, res) => {
      await getPayPalSubscription(req, res);
    });

    app.post("/api/paypal/subscription/check-activate", verifyToken, async (req, res) => {
      await checkAndActivateSubscription(req, res);
    });

    app.post("/api/paypal/subscription/:subscriptionId/cancel", verifyToken, async (req, res) => {
      await cancelPayPalSubscription(req, res);
    });

    console.log("PayPal subscription routes registered successfully");
  } else {
    console.warn("PayPal credentials not found - PayPal routes not registered");
  }

  // Associate anonymous demo with user account
  app.post("/api/demo/associate", verifyToken, async (req: any, res) => {
    try {
      const { demoId, convertToken } = req.body;

      if (!demoId || !convertToken) {
        return res.status(400).json({ message: "Demo ID and convert token are required" });
      }

      // Get the demo to verify it exists and is not expired
      const demo = await storage.getDemoPage(demoId);

      if (!demo) {
        return res.status(404).json({ message: "Demo not found" });
      }

      if (demo.convertToken !== convertToken) {
        return res.status(403).json({ message: "Invalid convert token" });
      }

      if (new Date(demo.expiresAt) < new Date()) {
        return res.status(410).json({ message: "Demo has expired" });
      }

      // Associate the demo with the current user
      const result = await storage.associateDemoWithUser(demoId, req.user.userId);

      if (result) {
        res.json({ message: "Demo successfully associated with your account" });
      } else {
        res.status(500).json({ message: "Failed to associate demo with account" });
      }
    } catch (error) {
      console.error("Associate demo error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Trial activation endpoint (no payment required)
  app.post("/api/trial/activate", verifyToken, async (req: any, res) => {
    try {
      const userId = req.user.userId;

      const profile = await storage.getProfile(userId);
      if (!profile) {
        return res.status(404).json({ message: "Profile not found" });
      }

      if (profile.trialStatus !== 'available') {
        return res.status(400).json({ 
          message: "Trial not available", 
          trialStatus: profile.trialStatus 
        });
      }

      if (profile.membershipStatus === 'pro') {
        return res.status(400).json({ 
          message: "You already have Pro membership" 
        });
      }

      const now = new Date();
      const trialEndsAt = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);

      await storage.updateProfile(userId, {
        membershipStatus: 'pro',
        membershipExpires: trialEndsAt.toISOString(),
        trialStatus: 'active',
        trialStartedAt: now.toISOString(),
        trialEndsAt: trialEndsAt.toISOString()
      });

      console.log(`âœ… Free trial activated for user ${userId} (no payment), ends at ${trialEndsAt.toISOString()}`);

      res.json({
        success: true,
        message: "Free trial activated successfully",
        trialEndsAt: trialEndsAt.toISOString()
      });
    } catch (error) {
      console.error("Trial activation error:", error);
      res.status(500).json({ message: "Failed to activate trial" });
    }
  });

  // Admin endpoints for debugging and support
  app.post("/api/admin/validate", async (req, res) => {
    try {
      const adminKey = req.headers['x-admin-key'];

      if (adminKey !== process.env.ADMIN_KEY) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      res.json({ message: "Valid admin key", authenticated: true });
    } catch (error) {
      console.error("Admin validation error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/admin/user-status/:userId", async (req, res) => {
    try {
      const adminKey = req.headers['x-admin-key'];

      if (adminKey !== process.env.ADMIN_KEY) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const userId = req.params.userId;

      // Get user profile
      const profile = await storage.getProfile(userId);
      if (!profile) {
        return res.status(404).json({ message: "User not found" });
      }

      // Get user subscriptions
      const subscriptions = await storage.getSubscriptionsByUser(userId);

      // Get user payments
      const payments = await storage.getPaymentsByUser(userId);

      res.json({
        profile: {
          id: profile.id,
          email: profile.email,
          fullName: profile.fullName,
          membershipStatus: profile.membershipStatus,
          membershipExpires: profile.membershipExpires,
          trialStatus: profile.trialStatus,
          trialStartedAt: profile.trialStartedAt,
          trialEndsAt: profile.trialEndsAt,
        },
        subscriptions: subscriptions.map(sub => ({
          id: sub.id,
          status: sub.status,
          planName: sub.planName,
          amount: sub.amount,
          currency: sub.currency,
          isTrial: sub.isTrial,
          trialEndsAt: sub.trialEndsAt,
          createdAt: sub.createdAt,
        })),
        payments: payments.map(payment => ({
          id: payment.id,
          amount: payment.amount,
          currency: payment.currency,
          status: payment.status,
          plan: payment.plan,
          createdAt: payment.createdAt,
        })),
      });
    } catch (error) {
      console.error("Admin user status error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Admin endpoint to cancel a subscription
  app.post("/api/admin/cancel-subscription", async (req, res) => {
    try {
      const adminKey = req.headers['x-admin-key'];

      if (adminKey !== process.env.ADMIN_KEY) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const { userId, subscriptionId } = req.body;

      if (!userId || !subscriptionId) {
        return res.status(400).json({ message: "userId and subscriptionId are required" });
      }

      // Update subscription status
      await storage.updateSubscription(subscriptionId, { status: 'cancelled' });

      // Downgrade user to free
      await storage.updateProfile(userId, {
        membershipStatus: 'free',
        membershipExpires: null
      });

      console.log(`Admin cancelled subscription ${subscriptionId} for user ${userId}`);

      res.json({ 
        success: true, 
        message: "Subscription cancelled successfully" 
      });
    } catch (error) {
      console.error("Admin cancel subscription error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Admin endpoint to update membership expiry
  app.post("/api/admin/update-expiry", async (req, res) => {
    try {
      const adminKey = req.headers['x-admin-key'];

      if (adminKey !== process.env.ADMIN_KEY) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const { userId, expiryDate } = req.body;

      if (!userId || !expiryDate) {
        return res.status(400).json({ message: "userId and expiryDate are required" });
      }

      // Update membership expiry
      await storage.updateProfile(userId, {
        membershipExpires: new Date(expiryDate).toISOString()
      });

      console.log(`Admin updated expiry for user ${userId} to ${expiryDate}`);

      res.json({ 
        success: true, 
        message: "Membership expiry updated successfully" 
      });
    } catch (error) {
      console.error("Admin update expiry error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Admin endpoint to get recent payments for notifications
  app.get("/api/admin/recent-payments", async (req, res) => {
    try {
      const adminKey = req.headers['x-admin-key'];

      if (adminKey !== process.env.ADMIN_KEY) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const limit = parseInt(req.query.limit as string) || 20;

      const allPayments = await storage.getAllRecentPayments(limit);

      const paymentsWithUserDetails = await Promise.all(
        allPayments.map(async (payment) => {
          const profile = payment.userId ? await storage.getProfile(payment.userId) : null;
          return {
            id: payment.id,
            userId: payment.userId,
            userEmail: profile?.email || 'Unknown',
            userFullName: profile?.fullName || 'Unknown User',
            amount: payment.amount,
            currency: payment.currency,
            status: payment.status,
            plan: payment.plan,
            paymentMethod: payment.paymentMethod,
            createdAt: payment.createdAt,
          };
        })
      );

      res.json({ payments: paymentsWithUserDetails });
    } catch (error) {
      console.error("Admin recent payments error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Admin endpoint to generate login token for a user (for debugging)
  app.post("/api/admin/generate-login-token", async (req, res) => {
    try {
      const adminKey = req.headers['x-admin-key'];

      if (adminKey !== process.env.ADMIN_KEY) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      if (!supabase) {
        return res.status(503).json({ message: "Supabase not configured" });
      }

      const { userId } = req.body;

      if (!userId) {
        return res.status(400).json({ message: "userId is required" });
      }

      // Generate a login link for the user
      const { data, error } = await supabase.auth.admin.generateLink({
        type: 'magiclink',
        email: userId, // This should be the user's email, but we're using ID
      });

      if (error) {
        console.error("Generate login token error:", error);
        return res.status(500).json({ message: "Failed to generate login token", error: error.message });
      }

      res.json({
        message: "Login token generated successfully",
        loginUrl: data.properties?.action_link,
        note: "This link can be used to login as the user for debugging purposes"
      });
    } catch (error) {
      console.error("Admin generate token error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

