          // Handle legacy {banners, logos, images} structure
          if (typeof rawGallery === 'object' && rawGallery !== null) {
            const legacy = rawGallery as any;
            const allImages = [
              ...(legacy.banners || []),
              ...(legacy.logos || []),
              ...(legacy.images || [])
            ];
            // Extract URLs from objects or use strings directly
            return allImages.map((item: any) => 
