    hsl(217.2, 91.2%, 95%) 25%,
    hsl(221.2, 83.2%, 88%) 50%, 
    hsl(217.2, 91.2%, 75%) 75%, 
    hsl(221.2, 83.2%, 65%) 100%);
}

.notification-badge {
  background: linear-gradient(135deg, 
    hsl(217.2, 91.2%, 59.8%) 0%, 
    hsl(221.2, 83.2%, 53.3%) 100%);
  box-shadow: 0 4px 14px hsl(217.2, 91.2%, 59.8% / 0.3);
  border: 1px solid hsl(217.2, 91.2%, 70%);
}

/* Glass Prism Effects */
.glass-prism {
  background: linear-gradient(145deg, 
    hsla(0, 0%, 100%, 0.25) 0%, 
    hsla(0, 0%, 100%, 0.1) 50%,
    hsla(0, 0%, 100%, 0.05) 100%);
  backdrop-filter: blur(20px);
  border: 1px solid hsla(0, 0%, 100%, 0.18);
  box-shadow: 
    0 8px 32px hsla(0, 0%, 0%, 0.1),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.4);
}

.glass-prism-card {
  background: linear-gradient(145deg, 
    hsla(217.2, 91.2%, 98%, 0.8) 0%, 
    hsla(217.2, 91.2%, 96%, 0.6) 100%);
  backdrop-filter: blur(16px);
  border: 1px solid hsla(217.2, 91.2%, 80%, 0.3);
  box-shadow: 
    0 8px 32px hsla(217.2, 91.2%, 20%, 0.1),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.6);
}

/* Enhanced Glass Prism Button */
.glass-prism-button {
  background: linear-gradient(145deg, 
    hsla(217.2, 91.2%, 75%, 0.9) 0%, 
    hsla(221.2, 83.2%, 65%, 0.8) 50%,
    hsla(217.2, 91.2%, 55%, 0.9) 100%) !important;
  backdrop-filter: blur(15px) !important;
  border: 2px solid hsla(0, 0%, 100%, 0.3) !important;
  box-shadow: 
    0 12px 24px hsla(217.2, 91.2%, 20%, 0.2),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.5) !important;
  color: white !important;
  font-weight: 600 !important;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1) !important;
  transform: perspective(1000px) rotateX(0deg) rotateY(0deg);
}

.glass-prism-button:hover {
  background: linear-gradient(145deg, 
    hsla(217.2, 91.2%, 65%, 0.95) 0%, 
    hsla(221.2, 83.2%, 55%, 0.9) 50%,
    hsla(217.2, 91.2%, 45%, 0.95) 100%) !important;
  transform: perspective(1000px) rotateX(-2deg) rotateY(2deg) translateY(-8px) scale(1.05) !important;
  box-shadow: 
    0 20px 40px hsla(217.2, 91.2%, 20%, 0.3),
    inset 0 2px 0 hsla(0, 0%, 100%, 0.6) !important;
  border-color: hsla(0, 0%, 100%, 0.5) !important;
  color: white !important;
}

.glass-prism-button:active {
  transform: perspective(1000px) rotateX(2deg) rotateY(-1deg) translateY(-4px) scale(0.98) !important;
  transition: all 0.1s ease !important;
}

/* Scroll Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

.animate-fade-in-up {
  animation: fadeInUp 0.6s ease-out forwards;
}

.animate-slide-in-left {
  animation: slideInLeft 0.6s ease-out forwards;
}

.animate-slide-in-right {
  animation: slideInRight 0.6s ease-out forwards;
}

.animate-scale-in {
  animation: scaleIn 0.5s ease-out forwards;
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

/* Enhanced Hover Effects */
.hover-lift {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.hover-lift:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 40px hsla(217.2, 91.2%, 20%, 0.15);
}

/* Blue Gradient Text */
.text-blue-gradient {
  background: linear-gradient(135deg, 
    hsl(217.2, 91.2%, 59.8%) 0%, 
    hsl(221.2, 83.2%, 53.3%) 50%,
    hsl(217.2, 91.2%, 45%) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Hero Radial Beam - Strong gradient behind headline */
.hero-radial-beam {
  position: relative;
  z-index: 10;
}

.hero-radial-beam::before {
  content: '';
  position: absolute;
  top: -150px;
  left: 50%;
  transform: translateX(-50%);
  width: 1200px;
  height: 800px;
  background: radial-gradient(ellipse 900px 500px at 50% 30%, 
    hsla(217, 70%, 92%, 0.4) 0%, 
    hsla(217, 60%, 90%, 0.25) 25%,
    hsla(221, 65%, 88%, 0.15) 45%,
    hsla(217, 55%, 85%, 0.08) 65%,
    transparent 85%);
  z-index: -1;
  pointer-events: none;
  filter: blur(1px);
}

/* Persistent page gradient for glass effects */
.page-gradient {
  background: linear-gradient(135deg, 
    hsl(0, 0%, 100%) 0%,
    hsl(217.2, 91.2%, 98%) 20%,
    hsl(217.2, 91.2%, 96%) 40%,
    hsl(221.2, 83.2%, 95%) 60%, 
    hsl(217.2, 91.2%, 94%) 80%, 
    hsl(221.2, 83.2%, 93%) 100%);
}

/* Enhanced overlay handling */
.bg-overlay {
  pointer-events: none;
  z-index: 0;
}

.content-layer {
  position: relative;
  z-index: 10;
}

/* Floating Glass Header Container */
.floating-glass-header-container {
  position: fixed;
  top: 16px;
  left: 0;
  right: 0;
  z-index: 50;
  display: flex;
  justify-content: center;
  pointer-events: none;
}

.floating-glass-header {
  background: linear-gradient(145deg, 
    hsla(0, 0%, 100%, 0.85) 0%, 
    hsla(217, 91%, 97%, 0.75) 50%,
    hsla(0, 0%, 100%, 0.8) 100%);
  backdrop-filter: blur(20px);
  border: 1px solid hsla(217, 91%, 80%, 0.2);
  box-shadow: 
    0 8px 32px hsla(217, 91%, 20%, 0.1),
    0 2px 8px hsla(217, 91%, 20%, 0.05),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.4);
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  border-radius: 20px;
  transform: translateY(0);
  pointer-events: auto;
  margin: 0 16px;
  width: 100%;
  max-width: 1200px;
}

.floating-glass-header:hover {
  transform: translateY(-2px);
  box-shadow: 
    0 12px 40px hsla(217, 91%, 20%, 0.15),
    0 4px 16px hsla(217, 91%, 20%, 0.08),
    inset 0 1px 0 hsla(0, 0%, 100%, 0.5);
}

.nav-link-animated {
  position: relative;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.nav-link-animated::after {
  content: '';
  position: absolute;
  width: 0;
  height: 2px;
  bottom: -4px;
  left: 50%;
  background: linear-gradient(90deg, 
    hsl(217.2, 91.2%, 59.8%) 0%, 
    hsl(221.2, 83.2%, 53.3%) 100%);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  transform: translateX(-50%);
}

.nav-link-animated:hover::after {
  width: 100%;
}

/* Responsive Animation Control */
@media (prefers-reduced-motion: reduce) {
  .animate-fade-in-up,
  .animate-slide-in-left,
  .animate-slide-in-right,
  .animate-scale-in,
  .animate-float,
  .animate-float-delayed,
  .animate-float-slow,
  .animate-fade-in,
  .animate-slide-up,
  .hover-lift {
    animation: none;
    transition: none;
  }
}

/* Additional Dashboard Animations */
@keyframes float-delayed {
  0%, 100% {
    transform: translateY(0px) translateX(0px);
  }
  50% {
    transform: translateY(-15px) translateX(10px);
  }
}

@keyframes float-slow {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(1deg);
  }
}

@keyframes fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-float-delayed {
  animation: float-delayed 8s ease-in-out infinite;
}

.animate-float-slow {
  animation: float-slow 10s ease-in-out infinite;
}

.animate-fade-in {
  animation: fade-in 0.5s ease-out;
}

.animate-slide-up {
  animation: slide-up 0.6s ease-out;
}

/* Mobile Utility Classes */
@layer utilities {
  /* Text clamping for responsive headings */
  .text-clamp-sm {
    font-size: clamp(1.5rem, 4vw, 2rem);
  }

  .text-clamp-md {
    font-size: clamp(1.75rem, 5vw, 3rem);
  }

  .text-clamp-lg {
    font-size: clamp(2rem, 6vw, 4rem);
  }

  .text-clamp-xl {
    font-size: clamp(2.5rem, 7vw, 5rem);
  }

  /* Text wrapping utilities */
  .text-wrap-balance {
    text-wrap: balance;
  }

  .text-break-words {
    overflow-wrap: anywhere;
    word-break: break-word;
    hyphens: auto;
  }

  /* Minimum touch target size (44x44px) */
  .min-touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Safe area padding for iOS */
  .safe-area-top {
    padding-top: env(safe-area-inset-top);
  }

  .safe-area-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .safe-area-left {
    padding-left: env(safe-area-inset-left);
  }

  .safe-area-right {
    padding-right: env(safe-area-inset-right);
  }

  /* Mobile-specific optimizations */
  @media (max-width: 640px) {
    /* Reduce blur effects on mobile for performance */
    .mobile-no-blur {
      backdrop-filter: none;
      filter: none;
    }

    /* Hide decorative elements on mobile */
    .mobile-hide {
      display: none;
    }

    /* Full width buttons on mobile */
    .mobile-full-width {
      width: 100%;
    }
  }

  /* Hide scrollbar but maintain scrolling functionality */
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }
}

