import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { CreditCard, Loader2 } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface RazorpaySubscriptionButtonProps {
  plan?: string;
  isTrial?: boolean;
  onSuccess?: (subscriptionId: string) => void;
  onError?: (error: any) => void;
  onCancel?: () => void;
  onPaymentStart?: () => void;
  disabled?: boolean;
  className?: string;
  children?: React.ReactNode;
}

declare global {
  interface Window {
    Razorpay: any;
  }
}

export default function RazorpaySubscriptionButton({
  plan = 'pro',
  isTrial = false,
  onSuccess,
  onError,
  onCancel,
  onPaymentStart,
  disabled = false,
  className = "",
  children = "Pay with Credit / Debit card"
}: RazorpaySubscriptionButtonProps) {
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();

  // Load Razorpay SDK
  const loadRazorpayScript = (): Promise<boolean> => {
    return new Promise((resolve) => {
      if (window.Razorpay) {
        resolve(true);
        return;
      }
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.onload = () => resolve(true);
      script.onerror = () => resolve(false);
      document.body.appendChild(script);
    });
  };

  const handleSubscription = async () => {
    try {
      setIsProcessing(true);

      // Load Razorpay script if not already loaded
      const scriptLoaded = await loadRazorpayScript();
      if (!scriptLoaded) {
        throw new Error('Failed to load Razorpay checkout script');
      }

      // Create subscription on backend
      const response = await apiRequest('POST', '/api/razorpay/subscriptions', {
        plan: plan,
        isTrial: isTrial
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to create subscription');
      }

      const subscriptionData = await response.json();

      // Configure Razorpay subscription options
      const options = {
        key: subscriptionData.key,
        subscription_id: subscriptionData.subscriptionId,
        name: 'BookingGen Pro',
        description: isTrial ? '7-Day Free Trial - Then $14.99/month' : 'Monthly Pro Subscription - $14.99/month',
        handler: async (response: any) => {
          try {
            console.log('Razorpay subscription response:', response);

            // Check subscription status with retry logic (Razorpay needs time to update status)
            const checkStatus = async (retries = 8, delay = 3000): Promise<void> => {
              for (let i = 0; i < retries; i++) {
                try {
                  // Wait before EACH check to give Razorpay time to update
                  // Start with 5 seconds for the first check, then 3 seconds for subsequent checks
                  const waitTime = i === 0 ? 5000 : delay;
                  console.log(`â±ï¸ Waiting ${waitTime/1000}s before status check ${i + 1}...`);
                  await new Promise(resolve => setTimeout(resolve, waitTime));

                  const statusResponse = await apiRequest('GET', `/api/razorpay/subscriptions/${subscriptionData.subscriptionId}`);
                  if (statusResponse.ok) {
                    const statusData = await statusResponse.json();
                    console.log(`âœ… Subscription status check (attempt ${i + 1}):`, statusData);

                    // If status is authenticated or active, activation is complete
                    if (statusData.status === 'authenticated' || statusData.status === 'active') {
                      console.log('ðŸŽ‰ Subscription activated successfully!');
                      return;
                    }

                    console.log(`â³ Status still "${statusData.status}", retrying...`);
                  } else {
                    const errorText = await statusResponse.text();
                    console.warn(`âŒ Status check HTTP ${statusResponse.status} (attempt ${i + 1}):`, errorText);
                  }
                } catch (statusError) {
                  console.error(`âŒ Status check failed (attempt ${i + 1}):`, statusError);
                }
              }

              console.log('âš ï¸ Status checks complete. Webhook will activate your membership shortly.');
            };

            // Start status check in background (don't await - let it run)
            checkStatus().catch(err => console.error('Status check error:', err));

            toast({
              title: isTrial ? "Free Trial Activated!" : "Subscription Created!",
              description: isTrial ? "Your 7-day free trial has started. You won't be charged until the trial ends." : "Your monthly Pro subscription is now active.",
            });

            if (onSuccess) {
              onSuccess(subscriptionData.subscriptionId);
            }
          } catch (error) {
            console.error('Subscription handler error:', error);
            toast({
              title: "Subscription Setup Failed",
              description: "There was an issue setting up your subscription. Please contact support.",
              variant: "destructive",
            });

            if (onError) {
              onError(error);
            }
          }
        },
        prefill: {
          name: '',
          email: '',
          contact: ''
        },
        notes: {
          plan: plan,
          type: 'subscription'
        },
        theme: {
          color: '#2563eb'
        },
        modal: {
          ondismiss: () => {
            setIsProcessing(false);
            toast({
              title: "Subscription Cancelled",
              description: "You can try again anytime.",
            });

            if (onCancel) {
              onCancel();
            }
          }
        }
      };

      // Open Razorpay subscription checkout
      const razorpayInstance = new window.Razorpay(options);
      razorpayInstance.open();

      // Call onPaymentStart callback to notify parent component that payment popup has opened
      if (onPaymentStart) {
        onPaymentStart();
      }

    } catch (error) {
      console.error('Subscription initialization failed:', error);
      setIsProcessing(false);

      toast({
        title: "Subscription Failed",
        description: error instanceof Error ? error.message : "Failed to initialize subscription. Please try again.",
        variant: "destructive",
      });

      if (onError) {
        onError(error);
      }
    }
  };

  return (
    <Button
      onClick={handleSubscription}
      disabled={disabled || isProcessing}
      className={className}
      data-testid="button-razorpay-subscription"
    >
      {isProcessing ? (
        <>
          <Loader2 className="h-4 w-4 mr-2 animate-spin" />
          Setting up subscription...
        </>
      ) : (
        <>
          <CreditCard className="h-4 w-4 mr-2" />
          {children}
        </>
      )}
    </Button>
  );
}

client/src/pages/signup.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { useAuth } from '@/hooks/use-auth';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { useToast } from '@/hooks/use-toast';
import { CalendarDays, Home, Eye, EyeOff } from 'lucide-react';
import EmailConfirmationModal from '@/components/modals/email-confirmation-modal';

export default function Signup() {
  const [, setLocation] = useLocation();
  const { signup, signInWithGoogle } = useAuth();
  const { toast } = useToast();
  const [loading, setLoading] = useState(false);
  const [showEmailConfirmation, setShowEmailConfirmation] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    remember: false,
    agreeToTerms: false
  });
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.agreeToTerms) {
      toast({
        title: "Please accept the terms",
        description: "You must agree to the Terms of Service and Privacy Policy.",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);

    try {
      const result = await signup(formData.email, formData.password, formData.fullName);

      if (result.needsEmailConfirmation) {
        setShowEmailConfirmation(true);
        toast({
          title: "Check your email!",
          description: "We sent you a confirmation link to complete your signup.",
        });
      } else {
        // Check if redirected from demo creation
        const urlParams = new URLSearchParams(window.location.search);
        const redirectFrom = urlParams.get('redirect_from');

        if (redirectFrom === 'demo') {
          toast({
            title: "Account created!",
            description: "Welcome to BookingGen! Your demo page is now available in your dashboard.",
          });
        } else {
          toast({
            title: "Account created!",
            description: "Welcome to BookingGen. Let's get you started.",
          });
        }
        setLocation('/dashboard');
      }
    } catch (error) {
      toast({
        title: "Signup failed",
        description: "Failed to create account. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignUp = async () => {
    try {
      await signInWithGoogle();
      // Note: Supabase OAuth will handle the redirect, so no need to manually redirect
    } catch (error) {
      toast({
        title: "Google Sign Up failed",
        description: "Failed to sign up with Google. Please try again.",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen page-gradient relative overflow-hidden">
      {/* Glass Prism Background Elements */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-50/80 via-white to-blue-50/80 bg-overlay"></div>
      <div className="absolute top-10 left-10 w-72 h-72 glass-prism rounded-full opacity-30 animate-float bg-overlay"></div>
      <div className="absolute top-32 right-20 w-96 h-96 glass-prism rounded-full opacity-20 animate-float bg-overlay" style={{animationDelay: '1.5s'}}></div>
      <div className="absolute bottom-20 left-1/3 w-48 h-48 glass-prism rounded-full opacity-25 animate-float bg-overlay" style={{animationDelay: '3s'}}></div>

      <div className="relative flex items-center justify-center min-h-screen p-6 content-layer">
        <div className="w-full max-w-md glass-prism-card rounded-3xl p-8 hover-lift animate-fade-in-up">
          <div className="flex justify-start mb-4">
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => setLocation('/')}
              className="flex items-center space-x-2 text-muted-foreground hover:text-foreground bg-white/10 backdrop-blur-sm border border-white/20 hover:bg-white/20 transition-all duration-300 rounded-lg"
              data-testid="button-back-home"
            >
              <Home className="h-4 w-4" />
              <span>Back to Homepage</span>
            </Button>
          </div>

          <div className="text-center mb-8">
            <div className="flex items-center justify-center space-x-3 mb-4">
              <div className="w-10 h-10 glass-prism rounded-lg flex items-center justify-center">
                <CalendarDays className="h-6 w-6 text-primary" />
              </div>
              <span className="text-xl font-bold text-foreground">BookingGen</span>
            </div>
            <h1 className="text-2xl font-bold text-foreground mb-2">Create your account</h1>
            <p className="text-muted-foreground">Start creating beautiful booking pages today</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <Label htmlFor="fullName" className="text-foreground font-medium">Full Name</Label>
              <Input
                id="fullName"
                type="text"
                placeholder="Enter your full name"
                value={formData.fullName}
                onChange={(e) => setFormData(prev => ({ ...prev, fullName: e.target.value }))}
                required
                data-testid="input-fullname"
                className="glass-input mt-2"
              />
            </div>

            <div>
              <Label htmlFor="email" className="text-foreground font-medium">Email</Label>
              <Input
                id="email"
                type="email"
                placeholder="Enter your email"
                value={formData.email}
                onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                required
                data-testid="input-email"
                className="glass-input mt-2"
              />
            </div>

            <div>
              <Label htmlFor="password" className="text-foreground font-medium">Password</Label>
              <div className="relative mt-2">
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="Create a password"
                  value={formData.password}
                  onChange={(e) => setFormData(prev => ({ ...prev, password: e.target.value }))}
                  required
                  data-testid="input-password"
                  className="glass-input pr-10"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                  data-testid="button-toggle-password"
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4 text-muted-foreground" />
                  ) : (
                    <Eye className="h-4 w-4 text-muted-foreground" />
                  )}
                </Button>
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center space-x-2">
                <Checkbox 
                  id="remember"
                  checked={formData.remember}
                  onCheckedChange={(checked) => setFormData(prev => ({ ...prev, remember: !!checked }))}
                  data-testid="checkbox-remember"
                />
                <Label htmlFor="remember" className="text-sm text-foreground">Remember me</Label>
              </div>

              <div className="flex items-start space-x-2">
                <Checkbox 
                  id="terms"
                  checked={formData.agreeToTerms}
                  onCheckedChange={(checked) => setFormData(prev => ({ ...prev, agreeToTerms: !!checked }))}
                  data-testid="checkbox-terms"
                />
                <Label htmlFor="terms" className="text-sm leading-5 text-foreground">
                  I agree to the{' '}
                  <Button variant="link" className="p-0 h-auto text-primary text-sm hover:text-primary/80 font-medium">
                    Terms of Service
                  </Button>
                  {' '}and{' '}
                  <Button variant="link" className="p-0 h-auto text-primary text-sm hover:text-primary/80 font-medium">
                    Privacy Policy
                  </Button>
                </Label>
              </div>
            </div>

            <Button 
              type="submit" 
              size="lg"
              className="w-full h-12 glass-prism-button px-6 py-2 rounded-full font-semibold text-[15px] shadow-md"
              disabled={loading}
              data-testid="button-create-account"
            >
              {loading ? "Creating Account..." : "Create Account"}
            </Button>

            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-white/20"></div>
              </div>
              <div className="relative flex justify-center text-sm">
                <span className="px-4 bg-white/5 backdrop-blur-sm text-muted-foreground rounded-full">Or continue with</span>
              </div>
            </div>

            <Button 
              type="button" 
              variant="outline" 
              size="lg"
              className="w-full h-12 bg-white/10 backdrop-blur-sm border border-white/20 hover:bg-white/20 transition-all duration-300 rounded-full"
              onClick={handleGoogleSignUp}
              data-testid="button-google-signup"
            >
              <svg className="mr-2 h-4 w-4" viewBox="0 0 24 24">
                <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
              Sign up with Google
            </Button>
          </form>

          <div className="text-center mt-6">
            <p className="text-muted-foreground">
              Already have an account?{' '}
              <Button 
                variant="link" 
                className="p-0 text-primary hover:text-primary/80 font-medium"
                onClick={() => setLocation('/login')}
                data-testid="link-signin"
              >
                Sign in
              </Button>
            </p>
          </div>
        </div>
      </div>

      {/* Email Confirmation Modal */}
      <EmailConfirmationModal
        isOpen={showEmailConfirmation}
        onClose={() => setShowEmailConfirmation(false)}
        email={formData.email}
      />
    </div>
  );
}

