import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon, Clock, User, Mail, Building2, Check, X, Filter } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { getUserAppointments } from '@/lib/supabase-queries';
import { useAuth } from '@/hooks/use-auth';

type ViewMode = 'month' | 'week' | 'day';

export default function CalendarView() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [viewMode, setViewMode] = useState<ViewMode>('month');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const { toast } = useToast();
  const { user } = useAuth();

  const { data: appointments = [] } = useQuery({
    queryKey: [`user-appointments-${user?.id}`],
    queryFn: getUserAppointments,
    enabled: !!user?.id,
  });

  const updateAppointmentMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const response = await apiRequest('PATCH', `/api/appointments/${id}`, { status });
      if (!response.ok) throw new Error('Failed to update appointment');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`user-appointments-${user?.id}`] });
      toast({
        title: 'Success',
        description: 'Appointment updated successfully.',
      });
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'Failed to update appointment.',
        variant: 'destructive',
      });
    },
  });

  const goToPreviousMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1));
  };

  const goToNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1));
  };

  const goToPreviousWeek = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(currentDate.getDate() - 7);
    setCurrentDate(newDate);
  };

  const goToNextWeek = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(currentDate.getDate() + 7);
    setCurrentDate(newDate);
  };

  const goToPreviousDay = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(currentDate.getDate() - 1);
    setCurrentDate(newDate);
  };

  const goToNextDay = () => {
    const newDate = new Date(currentDate);
    newDate.setDate(currentDate.getDate() + 1);
    setCurrentDate(newDate);
  };

  const goToToday = () => {
    setCurrentDate(new Date());
    setSelectedDate(new Date());
  };

  const handlePrevious = () => {
    if (viewMode === 'month') goToPreviousMonth();
    else if (viewMode === 'week') goToPreviousWeek();
    else goToPreviousDay();
  };

  const handleNext = () => {
    if (viewMode === 'month') goToNextMonth();
    else if (viewMode === 'week') goToNextWeek();
    else goToNextDay();
  };

  const getDaysInMonth = (date: Date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();
    return { daysInMonth, startingDayOfWeek, firstDay, lastDay };
  };

  const getWeekDays = (date: Date) => {
    const startOfWeek = new Date(date);
    startOfWeek.setDate(date.getDate() - date.getDay());
    const days = [];
    for (let i = 0; i < 7; i++) {
      const day = new Date(startOfWeek);
      day.setDate(startOfWeek.getDate() + i);
      days.push(day);
    }
    return days;
  };

  const { daysInMonth, startingDayOfWeek } = getDaysInMonth(currentDate);

  const getAppointmentsForDate = (date: Date) => {
    const appointmentsArray = appointments as any[];
    let filtered = appointmentsArray.filter((apt: any) => {
      const aptDate = new Date(apt.date);
      return (
        aptDate.getDate() === date.getDate() &&
        aptDate.getMonth() === date.getMonth() &&
        aptDate.getFullYear() === date.getFullYear()
      );
    });

    if (statusFilter !== 'all') {
      filtered = filtered.filter((apt: any) => apt.status === statusFilter);
    }

    return filtered;
  };

  const isToday = (date: Date) => {
    const today = new Date();
    return (
      date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear()
    );
  };

  const isSelected = (date: Date) => {
    if (!selectedDate) return false;
    return (
      date.getDate() === selectedDate.getDate() &&
      date.getMonth() === selectedDate.getMonth() &&
      date.getFullYear() === selectedDate.getFullYear()
    );
  };

  const monthYear = currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  const selectedDateAppointments = selectedDate ? getAppointmentsForDate(selectedDate) : [];

  const renderMonthView = () => (
    <div className="grid grid-cols-7 gap-2">
      {Array.from({ length: startingDayOfWeek }).map((_, index) => (
        <div key={`empty-${index}`} className="aspect-square" />
      ))}
      {Array.from({ length: daysInMonth }).map((_, index) => {
        const day = index + 1;
        const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
        const dayAppointments = getAppointmentsForDate(date);
        const hasAppointments = dayAppointments.length > 0;
        const isTodayDate = isToday(date);
        const isSelectedDate = isSelected(date);

        return (
          <button
            key={day}
            onClick={() => setSelectedDate(date)}
            className={`aspect-square p-2 rounded-xl transition-all duration-300 relative group ${
              isSelectedDate
                ? 'glass-prism-button text-white shadow-lg scale-105'
                : isTodayDate
                ? 'glass-prism bg-blue-500/10 border border-blue-500/30 text-blue-600 dark:text-blue-400 font-bold'
                : 'hover:glass-prism hover:scale-105 text-gray-700 dark:text-gray-300'
            }`}
            data-testid={`calendar-day-${day}`}
          >
            <span className="text-sm font-medium">{day}</span>
            {hasAppointments && (
              <div className="absolute bottom-0 left-0 right-0 h-1 flex gap-px px-1">
                {dayAppointments.slice(0, 3).map((apt: any, i: number) => (
                  <div
                    key={i}
                    className={`flex-1 rounded-sm ${
                      apt.status === 'accepted' 
                        ? 'bg-green-500' 
                        : apt.status === 'pending'
                        ? 'bg-yellow-500'
                        : 'bg-blue-500'
                    }`}
                  />
                ))}
              </div>
            )}
          </button>
        );
      })}
    </div>
  );

  const renderWeekView = () => {
    const weekDaysData = getWeekDays(currentDate);
    return (
      <div className="space-y-2">
        <div className="grid grid-cols-7 gap-2 mb-2">
          {weekDays.map((day) => (
            <div key={day} className="text-center text-sm font-semibold text-gray-600 dark:text-gray-400 py-2">
              {day}
            </div>
          ))}
        </div>
        <div className="grid grid-cols-7 gap-2">
          {weekDaysData.map((date, index) => {
            const dayAppointments = getAppointmentsForDate(date);
            const hasAppointments = dayAppointments.length > 0;
            const isTodayDate = isToday(date);
            const isSelectedDate = isSelected(date);

            return (
              <button
                key={index}
                onClick={() => setSelectedDate(date)}
                className={`p-4 rounded-xl transition-all duration-300 relative group min-h-[120px] ${
                  isSelectedDate
                    ? 'glass-prism-button text-white shadow-lg scale-105'
                    : isTodayDate
                    ? 'glass-prism bg-blue-500/10 border border-blue-500/30 text-blue-600 dark:text-blue-400'
                    : 'glass-prism hover:glass-prism-button hover:scale-105'
                }`}
                data-testid={`week-day-${index}`}
              >
                <div className="text-lg font-bold mb-2">{date.getDate()}</div>
                {hasAppointments && (
                  <div className="text-xs space-y-1">
                    {dayAppointments.slice(0, 2).map((apt: any, i: number) => (
                      <div 
                        key={i} 
                        className={`truncate px-1 py-0.5 rounded ${
                          apt.status === 'accepted' 
                            ? 'bg-green-500/30 border border-green-500/50' 
                            : 'bg-blue-500/20'
                        }`}
                      >
                        {apt.time}
                      </div>
                    ))}
                    {dayAppointments.length > 2 && (
                      <div className="text-xs opacity-70">+{dayAppointments.length - 2} more</div>
                    )}
                  </div>
                )}
              </button>
            );
          })}
        </div>
      </div>
    );
  };

  const renderDayView = () => {
    const dayAppointments = getAppointmentsForDate(currentDate);

    return (
      <div className="space-y-4">
        <div className="text-center mb-4">
          <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-100">
            {currentDate.toLocaleDateString('en-US', {
              weekday: 'long',
              month: 'long',
              day: 'numeric',
              year: 'numeric',
            })}
          </h3>
        </div>
        {dayAppointments.length > 0 ? (
          <div className="space-y-3">
            {dayAppointments.map((apt: any, index: number) => (
              <div
                key={apt.id}
                className="glass-prism p-4 rounded-xl hover:glass-prism-button transition-all duration-300 group animate-fade-in"
                style={{ animationDelay: `${index * 0.1}s` }}
                data-testid={`appointment-${apt.id}`}
              >
                <div className="flex items-start justify-between mb-2">
                  <h4 className="font-semibold text-gray-800 dark:text-gray-100">
                    {apt.serviceName}
                  </h4>
                  <Badge 
                    variant={apt.status === 'accepted' ? 'default' : 'secondary'} 
                    className={`text-xs capitalize ${apt.status === 'accepted' ? 'bg-green-600 hover:bg-green-700 text-white' : ''}`}
                  >
                    {apt.status}
                  </Badge>
                </div>
                <div className="space-y-2 text-sm text-gray-600 dark:text-gray-400">
                  <div className="flex items-center gap-2">
                    <Building2 className="h-3.5 w-3.5" />
                    <span>{apt.pageName}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <Clock className="h-3.5 w-3.5" />
                    <span>{apt.time}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <User className="h-3.5 w-3.5" />
                    <span>{apt.customerName}</span>
                  </div>
                  {apt.customerEmail && (
                    <div className="flex items-center gap-2">
                      <Mail className="h-3.5 w-3.5" />
                      <span className="truncate">{apt.customerEmail}</span>
                    </div>
                  )}
                </div>
                {apt.status === 'pending' && (
                  <div className="flex gap-2 mt-3 pt-3 border-t border-white/10">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => updateAppointmentMutation.mutate({ id: apt.id, status: 'accepted' })}
                      className="flex-1 glass-prism-button"
                      data-testid={`button-accept-${apt.id}`}
                    >
                      <Check className="h-3.5 w-3.5 mr-1" />
                      Accept
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => updateAppointmentMutation.mutate({ id: apt.id, status: 'declined' })}
                      data-testid={`button-reject-${apt.id}`}
                    >
                      <X className="h-3.5 w-3.5 mr-1" />
                      Reject
                    </Button>
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-16">
            <div className="w-20 h-20 glass-prism rounded-full flex items-center justify-center mx-auto mb-6 animate-pulse">
              <CalendarIcon className="h-10 w-10 text-gray-400" />
            </div>
            <p className="text-gray-600 dark:text-gray-400">No appointments on this day</p>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="space-y-6 animate-fade-in">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h2 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 dark:from-white dark:to-gray-300 bg-clip-text text-transparent">
            Calendar
          </h2>
          <p className="text-muted-foreground mt-1">View and manage your bookings</p>
        </div>
        <div className="flex gap-2 flex-wrap">
          <Select value={viewMode} onValueChange={(value: ViewMode) => setViewMode(value)}>
            <SelectTrigger className="w-[120px] glass-prism" data-testid="select-view-mode">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="month">Month</SelectItem>
              <SelectItem value="week">Week</SelectItem>
              <SelectItem value="day">Day</SelectItem>
            </SelectContent>
          </Select>
          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger className="w-[140px] glass-prism" data-testid="select-status-filter">
              <SelectValue placeholder="Filter by status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="accepted">Accepted</SelectItem>
              <SelectItem value="declined">Declined</SelectItem>
            </SelectContent>
          </Select>
          <Button onClick={goToToday} className="glass-prism-button text-white shadow-lg" data-testid="button-today">
            <CalendarIcon className="h-4 w-4 mr-2" />
            Today
          </Button>
        </div>
      </div>

      <div className="grid lg:grid-cols-3 gap-6">
        <Card className="lg:col-span-2 glass-prism-card backdrop-blur-xl bg-white/5 dark:bg-black/5 border-white/20 shadow-2xl animate-slide-in-left mobile-no-blur">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="text-xl font-bold text-gray-800 dark:text-gray-100">
                {viewMode === 'day'
                  ? currentDate.toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })
                  : viewMode === 'week'
                  ? `Week of ${getWeekDays(currentDate)[0].toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`
                  : monthYear}
              </CardTitle>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="icon"
                  onClick={handlePrevious}
                  className="glass-prism hover:glass-prism-button transition-all duration-300"
                  data-testid="button-prev"
                >
                  <ChevronLeft className="h-4 w-4" />
                </Button>
                <Button
                  variant="outline"
                  size="icon"
                  onClick={handleNext}
                  className="glass-prism hover:glass-prism-button transition-all duration-300"
                  data-testid="button-next"
                >
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {viewMode === 'month' && (
              <>
                <div className="grid grid-cols-7 gap-2 mb-2">
                  {weekDays.map((day) => (
                    <div key={day} className="text-center text-sm font-semibold text-gray-600 dark:text-gray-400 py-2">
                      {day}
                    </div>
                  ))}
                </div>
                {renderMonthView()}
              </>
            )}
            {viewMode === 'week' && renderWeekView()}
            {viewMode === 'day' && renderDayView()}
          </CardContent>
        </Card>

        <Card className="glass-prism-card backdrop-blur-xl bg-white/5 dark:bg-black/5 border-white/20 shadow-2xl animate-slide-in-right mobile-no-blur">
          <CardHeader>
            <CardTitle className="text-lg font-bold text-gray-800 dark:text-gray-100">
              {selectedDate
                ? selectedDate.toLocaleDateString('en-US', {
                    weekday: 'long',
                    month: 'long',
                    day: 'numeric',
                  })
                : 'Select a date'}
            </CardTitle>
          </CardHeader>
          <CardContent>
            {selectedDate && selectedDateAppointments.length > 0 ? (
              <div className="space-y-3">
                {selectedDateAppointments.map((apt: any, index: number) => (
                  <div
                    key={apt.id}
                    className="glass-prism p-4 rounded-xl hover:glass-prism-button transition-all duration-300 group animate-fade-in"
                    style={{ animationDelay: `${index * 0.1}s` }}
                    data-testid={`appointment-${apt.id}`}
                  >
                    <div className="flex items-start justify-between mb-2">
                      <h4 className="font-semibold text-gray-800 dark:text-gray-100">
                        {apt.serviceName}
                      </h4>
                      <Badge 
                        variant={apt.status === 'accepted' ? 'default' : 'secondary'} 
                        className={`text-xs capitalize ${apt.status === 'accepted' ? 'bg-green-600 hover:bg-green-700 text-white' : ''}`}
                      >
                        {apt.status}
                      </Badge>
                    </div>
                    <div className="space-y-2 text-sm text-gray-600 dark:text-gray-400">
                      <div className="flex items-center gap-2">
                        <Building2 className="h-3.5 w-3.5" />
                        <span>{apt.pageName}</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <Clock className="h-3.5 w-3.5" />
                        <span>{apt.time}</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <User className="h-3.5 w-3.5" />
                        <span>{apt.customerName}</span>
                      </div>
                      {apt.customerEmail && (
                        <div className="flex items-center gap-2">
                          <Mail className="h-3.5 w-3.5" />
                          <span className="truncate">{apt.customerEmail}</span>
                        </div>
                      )}
                    </div>
                    {apt.status === 'pending' && (
                      <div className="flex gap-2 mt-3 pt-3 border-t border-white/10">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => updateAppointmentMutation.mutate({ id: apt.id, status: 'accepted' })}
                          disabled={updateAppointmentMutation.isPending}
                          className="flex-1 glass-prism-button"
                          data-testid={`button-accept-appointment-${apt.id}`}
                        >
                          <Check className="h-3.5 w-3.5 mr-1" />
                          Accept
                        </Button>
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => updateAppointmentMutation.mutate({ id: apt.id, status: 'declined' })}
                          disabled={updateAppointmentMutation.isPending}
                          data-testid={`button-reject-appointment-${apt.id}`}
                        >
                          <X className="h-3.5 w-3.5 mr-1" />
                          Reject
                        </Button>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ) : selectedDate ? (
              <div className="text-center py-8">
                <div className="w-16 h-16 glass-prism rounded-full flex items-center justify-center mx-auto mb-4 animate-pulse">
                  <CalendarIcon className="h-8 w-8 text-gray-400" />
                </div>
                <p className="text-gray-600 dark:text-gray-400">No appointments on this date</p>
              </div>
            ) : (
              <div className="text-center py-8">
                <div className="w-16 h-16 glass-prism rounded-full flex items-center justify-center mx-auto mb-4">
                  <CalendarIcon className="h-8 w-8 text-gray-400" />
                </div>
                <p className="text-gray-600 dark:text-gray-400">Select a date to view appointments</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/ui/badge.tsx.content:
import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, uuid, timestamp, jsonb, numeric, integer, date, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const profiles = pgTable("profiles", {
  id: uuid("id").primaryKey(), // This will be the Supabase auth.users.id
  email: text("email"), // Store email for demo users
  fullName: text("full_name"),
  createdAt: timestamp("created_at").default(sql`now()`),
  membershipStatus: text("membership_status").default("free"), // free | demo | pro
  membershipPlan: text("membership_plan"),
  membershipExpires: timestamp("membership_expires"),
  trialStatus: text("trial_status").default("available"), // available | active | used | expired
  trialStartedAt: timestamp("trial_started_at"),
  trialEndsAt: timestamp("trial_ends_at"),
});

export const pages = pgTable("pages", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  ownerId: uuid("owner_id").references(() => profiles.id),
  title: text("title").notNull(),
  slug: text("slug").notNull().unique(),
  tagline: text("tagline"),
  logoUrl: text("logo_url"),
  primaryColor: text("primary_color").default("#2563eb"),
  theme: text("theme").default("Ocean Blue"),
  backgroundType: text("background_type").default("gradient"),
  backgroundValue: text("background_value").default("blue"),
  fontFamily: text("font_family").default("inter"),
  calendarLink: text("calendar_link"),
  locationLink: text("location_link"),
  faqs: jsonb("faqs").default(sql`'[]'`), // Array of {question: string, answer: string}
  acceptReviews: text("accept_reviews").default("true"), // "true" or "false" 
  businessHours: jsonb("business_hours").default(sql`'{"monday":"9:00-17:00","tuesday":"9:00-17:00","wednesday":"9:00-17:00","thursday":"9:00-17:00","friday":"9:00-17:00","saturday":"Closed","sunday":"Closed"}'`),
  contactPhone: text("contact_phone"),
  contactEmail: text("contact_email"),
  businessAddress: text("business_address"),
  cancellationPolicy: text("cancellation_policy"),
  showBusinessHours: text("show_business_hours").default("true"),
  showContactInfo: text("show_contact_info").default("true"),
  gallery: jsonb("gallery").default(sql`'{"logos": [], "banners": [], "images": []}'`), // Gallery object with logos, banners, and images arrays
  data: jsonb("data"),
  published: boolean("published").default(false),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const demoPages = pgTable("demo_pages", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  ownerId: uuid("owner_id").references(() => profiles.id), // Associate demo with user
  data: jsonb("data").notNull(), // Contains the demo page configuration
  convertToken: text("convert_token"), // Secure token for conversion (null when used)
  createdAt: timestamp("created_at").default(sql`now()`),
  expiresAt: timestamp("expires_at").default(sql`now() + interval '7 days'`),
});

export const services = pgTable("services", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  pageId: uuid("page_id").references(() => pages.id, { onDelete: "cascade" }),
  name: text("name").notNull(),
  description: text("description"),
  imageUrl: text("image_url"),
  durationMinutes: integer("duration_minutes").notNull(),
  price: numeric("price").notNull(),
  currency: text("currency").default("USD").notNull(),
  createdAt: timestamp("created_at").default(sql`now()`),
});

export const staff = pgTable("staff", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  pageId: uuid("page_id").references(() => pages.id, { onDelete: "cascade" }),
  name: text("name").notNull(),
  bio: text("bio"),
  imageUrl: text("image_url"),
  position: text("position"),
  email: text("email"),
  phone: text("phone"),
  displayOrder: integer("display_order").default(0),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const appointments = pgTable("appointments", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  pageId: uuid("page_id").references(() => pages.id, { onDelete: "cascade" }),
  ownerId: uuid("owner_id").references(() => profiles.id),
  serviceId: uuid("service_id").references(() => services.id),
  customerName: text("customer_name").notNull(),
  customerEmail: text("customer_email"),
  customerPhone: text("customer_phone").notNull(),
  date: date("date").notNull(),
  time: text("time").notNull(),
  status: text("status").default("pending"), // pending|accepted|declined|rescheduled
  notes: text("notes"),
  googleEventId: text("google_event_id"), // Google Calendar event ID if synced
  syncedFromGoogle: boolean("synced_from_google").default(false),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const reviews = pgTable("reviews", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  pageId: uuid("page_id").references(() => pages.id, { onDelete: "cascade" }),
  reviewerName: text("reviewer_name").notNull(),
  reviewerEmail: text("reviewer_email"),
  rating: integer("rating").notNull(), // 1-5 stars
  reviewText: text("review_text"),
  status: text("status").default("pending"), // Keep existing status column
  isApproved: text("is_approved").default("pending"), // pending|approved|rejected
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const paymentsDemo = pgTable("payments_demo", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: uuid("user_id").references(() => profiles.id),
  amount: numeric("amount"),
  currency: text("currency"),
  status: text("status"),
  paymentIntentId: text("payment_intent_id"), // Legacy Stripe field
  subscriptionId: text("subscription_id"), // Legacy subscription field
  paymentId: text("payment_id"), // Razorpay payment ID
  paymentMethod: text("payment_method"), // Payment method used (razorpay, razorpay_subscription, etc.)
  plan: text("plan"),
  paypalOrderId: text("paypal_order_id"), // Legacy field - PayPal no longer used
  paypalPaymentId: text("paypal_payment_id"), // Legacy field - PayPal no longer used
  meta: jsonb("meta"),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const subscriptions = pgTable("subscriptions", {
  id: text("id").primaryKey(), // Legacy PayPal subscription ID - PayPal no longer used
  userId: uuid("user_id").references(() => profiles.id),
  status: text("status").notNull(), // APPROVAL_PENDING|ACTIVE|CANCELLED|SUSPENDED|EXPIRED
  planId: text("plan_id").notNull(), // Legacy PayPal plan ID - PayPal no longer used
  currentPeriodStart: timestamp("current_period_start"), // Keep existing column
  currentPeriodEnd: timestamp("current_period_end"), // Keep existing column
  planName: text("plan_name"), // "pro", "premium", etc.
  currency: text("currency").default("USD"),
  amount: numeric("amount").notNull(),
  startTime: timestamp("start_time"),
  nextBillingTime: timestamp("next_billing_time"),
  isTrial: boolean("is_trial").default(false),
  trialEndsAt: timestamp("trial_ends_at"),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

// Legacy tables that exist in the database - keep to prevent data loss
export const memberships = pgTable("memberships", {
  userId: uuid("user_id").primaryKey().references(() => profiles.id),
  active: boolean("active").default(true).notNull(),
  plan: text("plan"),
  purchasedAt: timestamp("purchased_at", { withTimezone: true }).default(sql`now()`).notNull(),
  expiresAt: timestamp("expires_at", { withTimezone: true }),
  transactionId: text("transaction_id"),
  createdAt: timestamp("created_at", { withTimezone: true }).default(sql`now()`),
  updatedAt: timestamp("updated_at", { withTimezone: true }).default(sql`now()`),
});

export const bookingPages = pgTable("booking_pages", {
  id: text("id").primaryKey(),
  ownerId: uuid("owner_id").notNull().references(() => profiles.id),
  slug: text("slug").notNull(),
  businessName: text("business_name").notNull(),
  tagline: text("tagline"),
  logoDataUrl: text("logo_data_url"),
  theme: jsonb("theme"),
  services: jsonb("services"),
  contact: jsonb("contact"),
  calendarUrl: text("calendar_url"),
  buttonText: text("button_text"),
  createdAt: timestamp("created_at", { withTimezone: true }).default(sql`now()`),
  updatedAt: timestamp("updated_at", { withTimezone: true }).default(sql`now()`),
});

export const notes = pgTable("notes", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: uuid("user_id").references(() => profiles.id, { onDelete: "cascade" }).notNull(),
  title: text("title").notNull(),
  content: text("content").default(""),
  category: text("category").default("General"),
  tags: text("tags").default(""),
  color: text("color").default("blue"),
  isPinned: boolean("is_pinned").default(false),
  isArchived: boolean("is_archived").default(false),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

// Insert schemas (users are managed by Supabase auth)

export const insertProfileSchema = createInsertSchema(profiles).omit({
  createdAt: true,
});

export const insertPageSchema = createInsertSchema(pages).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
}).extend({
  locationLink: z.string().optional().refine((val) => {
    if (!val || val.trim() === '') return true;
    try {
      new URL(val);
      return true;
    } catch {
      return false;
    }
  }, {
    message: "Location link must be a valid URL (e.g., https://maps.google.com/...)"
  })
});

export const insertServiceSchema = createInsertSchema(services).omit({
  id: true,
  createdAt: true,
});

export const insertStaffSchema = createInsertSchema(staff).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertAppointmentSchema = createInsertSchema(appointments).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertReviewSchema = createInsertSchema(reviews).omit({
  id: true,
  createdAt: true,
});

export const insertPaymentSchema = createInsertSchema(paymentsDemo).omit({
  id: true,
  createdAt: true,
});

export const insertSubscriptionSchema = createInsertSchema(subscriptions).omit({
  createdAt: true,
  updatedAt: true,
});

export const insertDemoPageSchema = createInsertSchema(demoPages).omit({
  id: true,
  createdAt: true,
  expiresAt: true,
});

export const insertNoteSchema = createInsertSchema(notes).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

// Types (User type managed by Supabase auth)
export type Profile = typeof profiles.$inferSelect;
export type InsertProfile = z.infer<typeof insertProfileSchema>;
export type Page = typeof pages.$inferSelect;
export type InsertPage = z.infer<typeof insertPageSchema>;
export type Service = typeof services.$inferSelect;
export type InsertService = z.infer<typeof insertServiceSchema>;
export type Staff = typeof staff.$inferSelect;
export type InsertStaff = z.infer<typeof insertStaffSchema>;
export type Appointment = typeof appointments.$inferSelect;
export type InsertAppointment = z.infer<typeof insertAppointmentSchema>;
export type Review = typeof reviews.$inferSelect;
export type InsertReview = z.infer<typeof insertReviewSchema>;
export type Payment = typeof paymentsDemo.$inferSelect;
export type InsertPayment = z.infer<typeof insertPaymentSchema>;
export type Subscription = typeof subscriptions.$inferSelect;
export type InsertSubscription = z.infer<typeof insertSubscriptionSchema>;
export type DemoPage = typeof demoPages.$inferSelect;
export type InsertDemoPage = z.infer<typeof insertDemoPageSchema>;
export type Note = typeof notes.$inferSelect;
export type InsertNote = z.infer<typeof insertNoteSchema>;

