import { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { Calendar, CheckCircle2, Loader2, RefreshCw, Unplug } from 'lucide-react';
import { SiGooglecalendar } from 'react-icons/si';
import { useLocation } from 'wouter';
import googleCalendarIcon from '@assets/image_1760024088726.png';

interface GoogleEvent {
  googleEventId: string;
  title: string;
  date: string;
  startTime: string;
  endTime: string;
  description: string;
  location: string;
  attendees: string;
}

export default function GoogleCalendarPage() {
  const { toast } = useToast();
  const [location, setLocation] = useLocation();
  const [showSuccess, setShowSuccess] = useState(false);

  // Check connection status
  const { data: statusData, isLoading: isCheckingStatus } = useQuery({
    queryKey: ['/api/google/status'],
  });

  const isConnected = statusData?.connected || false;

  // Get calendar events
  const { data: eventsData, isLoading: isLoadingEvents, refetch: refetchEvents } = useQuery({
    queryKey: ['/api/google/events'],
    enabled: isConnected,
  });

  const events: GoogleEvent[] = eventsData?.events || [];

  // Connect to Google Calendar
  const connectMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('GET', '/api/google/auth');
      return response.json();
    },
    onSuccess: (data) => {
      if (data.url) {
        window.location.href = data.url;
      }
    },
    onError: (error) => {
      console.error('Google connect error:', error);
      toast({
        title: 'Connection failed',
        description: 'Failed to connect to Google Calendar. Please try again.',
        variant: 'destructive',
      });
    },
  });

  // Disconnect from Google Calendar
  const disconnectMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/google/disconnect', {});
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/google/status'] });
      toast({
        title: 'Disconnected',
        description: 'Google Calendar has been disconnected.',
      });
    },
    onError: () => {
      toast({
        title: 'Disconnect failed',
        description: 'Failed to disconnect Google Calendar. Please try again.',
        variant: 'destructive',
      });
    },
  });

  // Sync events to appointments
  const syncMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/google/sync', {});
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
      toast({
        title: 'Sync complete',
        description: `${data.syncedCount || 0} events synced to your appointments.`,
      });
      refetchEvents();
    },
    onError: () => {
      toast({
        title: 'Sync failed',
        description: 'Failed to sync calendar events. Please try again.',
        variant: 'destructive',
      });
    },
  });

  // Handle callback redirect
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const connected = params.get('connected');
    const error = params.get('error');

    if (connected === 'true') {
      setShowSuccess(true);
      queryClient.invalidateQueries({ queryKey: ['/api/google/status'] });
      toast({
        title: 'Connected!',
        description: 'Google Calendar connected successfully.',
      });

      // Clean up URL
      setLocation('/dashboard/google-calendar');

      // Hide success animation after 3 seconds
      setTimeout(() => setShowSuccess(false), 3000);
    } else if (error) {
      toast({
        title: 'Connection failed',
        description: 'Failed to connect to Google Calendar. Please try again.',
        variant: 'destructive',
      });

      // Clean up URL
      setLocation('/dashboard/google-calendar');
    }
  }, []);

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  if (isCheckingStatus) {
    return (
      <div className="flex items-center justify-center h-96">
        <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6">
      <div>
        <h1 className="text-3xl font-bold text-gray-900 dark:text-gray-100">Google Calendar</h1>
        <p className="text-gray-600 dark:text-gray-400 mt-2">
          Sync your Google Calendar events with BookingGen
        </p>
      </div>

      {!isConnected ? (
        <Card 
          className="glass-prism-card backdrop-blur-xl bg-white/40 dark:bg-black/40 border-white/20 shadow-2xl hover:shadow-3xl transition-all duration-300 hover:scale-[1.02] max-w-2xl mx-auto"
          data-testid="card-google-connect"
        >
          <CardHeader className="text-center pb-4">
            <div className="mx-auto w-24 h-24 flex items-center justify-center mb-4">
              <img src={googleCalendarIcon} alt="Google Calendar" className="w-20 h-20" />
            </div>
            <CardTitle className="text-2xl font-bold text-gray-900 dark:text-gray-100">
              Connect Google Calendar
            </CardTitle>
            <CardDescription className="text-gray-600 dark:text-gray-400 text-base">
              Securely connect your Google Calendar to automatically sync appointments
            </CardDescription>
          </CardHeader>
          <CardContent className="flex flex-col items-center space-y-4 pb-8">
            <Button 
              onClick={() => connectMutation.mutate()}
              disabled={connectMutation.isPending}
              className="glass-prism-button backdrop-blur-lg bg-[#4285f4] hover:bg-[#357ae8] text-white shadow-xl px-8 py-6 text-lg font-semibold"
              data-testid="button-connect-google"
            >
              {connectMutation.isPending ? (
                <>
                  <Loader2 className="mr-3 h-6 w-6 animate-spin" />
                  Connecting...
                </>
              ) : (
                <>
                  <img src={googleCalendarIcon} alt="" className="w-6 h-6 mr-3" />
                  Sign in with Google Calendar
                </>
              )}
            </Button>
            <p className="text-sm text-gray-500 dark:text-gray-400 text-center max-w-md">
              We'll only access your calendar events to help you manage your appointments better.
              You can disconnect anytime.
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-6">
          {/* Success Animation */}
          {showSuccess && (
            <div className="fixed inset-0 flex items-center justify-center bg-black/50 backdrop-blur-sm z-50 animate-in fade-in">
              <div className="bg-white dark:bg-gray-800 rounded-full p-8 shadow-2xl animate-in zoom-in">
                <CheckCircle2 className="h-24 w-24 text-green-500 animate-pulse" />
              </div>
            </div>
          )}

          {/* Connected Status Card */}
          <Card className="glass-prism-card backdrop-blur-xl bg-white/40 dark:bg-black/40 border-white/20 shadow-xl">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-12 h-12 rounded-full bg-green-500 flex items-center justify-center">
                    <CheckCircle2 className="h-6 w-6 text-white" />
                  </div>
                  <div>
                    <CardTitle className="text-xl text-gray-900 dark:text-gray-100">Connected</CardTitle>
                    <CardDescription>Google Calendar is synced</CardDescription>
                  </div>
                </div>
                <div className="flex space-x-2">
                  <Button
                    onClick={() => syncMutation.mutate()}
                    disabled={syncMutation.isPending}
                    variant="outline"
                    className="glass-prism-button"
                    data-testid="button-sync-events"
                  >
                    {syncMutation.isPending ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <RefreshCw className="h-4 w-4" />
                    )}
                    <span className="ml-2">Sync Now</span>
                  </Button>
                  <Button
                    onClick={() => disconnectMutation.mutate()}
                    disabled={disconnectMutation.isPending}
                    variant="outline"
                    className="text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20"
                    data-testid="button-disconnect-google"
                  >
                    <Unplug className="h-4 w-4 mr-2" />
                    Disconnect
                  </Button>
                </div>
              </div>
            </CardHeader>
          </Card>

          {/* Events List */}
          <Card className="glass-prism-card backdrop-blur-xl bg-white/40 dark:bg-black/40 border-white/20 shadow-xl">
            <CardHeader>
              <CardTitle className="text-2xl text-gray-900 dark:text-gray-100 flex items-center">
                <Calendar className="h-6 w-6 mr-2" />
                Your Google Calendar Events
              </CardTitle>
              <CardDescription>
                Recent events from your Google Calendar
              </CardDescription>
            </CardHeader>
            <CardContent>
              {isLoadingEvents ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
                </div>
              ) : events.length === 0 ? (
                <div className="text-center py-12">
                  <Calendar className="h-12 w-12 mx-auto text-gray-400 mb-4" />
                  <p className="text-gray-600 dark:text-gray-400">
                    No events found in your Google Calendar
                  </p>
                </div>
              ) : (
                <div className="space-y-3">
                  {events.map((event) => (
                    <div
                      key={event.googleEventId}
                      className="glass-prism-card backdrop-blur-lg bg-white/60 dark:bg-black/60 border-white/30 p-4 rounded-xl hover:shadow-lg transition-all duration-200"
                      data-testid={`event-${event.googleEventId}`}
                    >
                      <div className="flex justify-between items-start">
                        <div className="flex-1">
                          <h3 className="font-semibold text-gray-900 dark:text-gray-100" data-testid={`event-title-${event.googleEventId}`}>
                            {event.title}
                          </h3>
                          <div className="mt-2 space-y-1 text-sm text-gray-600 dark:text-gray-400">
                            <p data-testid={`event-date-${event.googleEventId}`}>
                              <span className="font-medium">Date:</span> {formatDate(event.date)}
                            </p>
                            <p data-testid={`event-time-${event.googleEventId}`}>
                              <span className="font-medium">Time:</span> {event.startTime} - {event.endTime}
                            </p>
                            {event.location && (
                              <p data-testid={`event-location-${event.googleEventId}`}>
                                <span className="font-medium">Location:</span> {event.location}
                              </p>
                            )}
                            {event.description && (
                              <p className="text-gray-500 dark:text-gray-400 mt-1">
                                {event.description}
                              </p>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

server/google-oauth.ts.content:
import { google } from 'googleapis';
import { storage } from './storage';

const GOOGLE_CLIENT_ID = process.env.GOOGLE_CLIENT_ID;
const GOOGLE_CLIENT_SECRET = process.env.GOOGLE_CLIENT_SECRET;

if (!GOOGLE_CLIENT_ID || !GOOGLE_CLIENT_SECRET) {
  console.warn('Google OAuth credentials not found - Google Calendar integration will be unavailable');
}

// Determine the correct redirect URI
const redirectUri = process.env.REPLIT_DEV_DOMAIN 
  ? `https://${process.env.REPLIT_DEV_DOMAIN}/api/google/callback`
  : 'http://localhost:5000/api/google/callback';

console.log('Google OAuth redirect URI configured as:', redirectUri);

// OAuth2 client configuration
const oauth2Client = new google.auth.OAuth2(
  GOOGLE_CLIENT_ID,
  GOOGLE_CLIENT_SECRET,
  redirectUri
);

const SCOPES = [
  'https://www.googleapis.com/auth/calendar.readonly',
  'https://www.googleapis.com/auth/calendar.events'
];

export function getAuthUrl(state: string): string {
  return oauth2Client.generateAuthUrl({
    access_type: 'offline',
    scope: SCOPES,
    state,
    prompt: 'consent'
  });
}

export async function getTokensFromCode(code: string) {
  const { tokens } = await oauth2Client.getToken(code);
  return tokens;
}

export async function refreshAccessToken(refreshToken: string) {
  oauth2Client.setCredentials({ refresh_token: refreshToken });
  const { credentials } = await oauth2Client.refreshAccessToken();
  return credentials;
}

export async function getCalendarEvents(accessToken: string, refreshToken: string, userId: string) {
  try {
    oauth2Client.setCredentials({
      access_token: accessToken,
      refresh_token: refreshToken
    });

    const calendar = google.calendar({ version: 'v3', auth: oauth2Client });

    const now = new Date();
    const oneMonthAgo = new Date();
    oneMonthAgo.setMonth(oneMonthAgo.getMonth() - 1);

    const response = await calendar.events.list({
      calendarId: 'primary',
      timeMin: oneMonthAgo.toISOString(),
      maxResults: 100,
      singleEvents: true,
      orderBy: 'startTime',
    });

    const events = response.data.items || [];

    // Map and filter events
    const mappedEvents = events
      .filter(event => event.start && (event.start.dateTime || event.start.date))
      .map(event => {
        const startDateTime = event.start?.dateTime || event.start?.date;
        const endDateTime = event.end?.dateTime || event.end?.date;

        const startDate = new Date(startDateTime!);
        const endDate = new Date(endDateTime!);

        // Extract date and time
        const date = startDate.toISOString().split('T')[0];
        const startTime = event.start?.dateTime ? 
          startDate.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false }) : 
          '00:00';
        const endTime = event.end?.dateTime ? 
          endDate.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false }) : 
          '23:59';

        return {
          googleEventId: event.id,
          title: event.summary || 'Untitled Event',
          date,
          startTime,
          endTime,
          description: event.description || '',
          location: event.location || '',
          attendees: event.attendees?.map(a => a.email).join(', ') || ''
        };
      });

    return mappedEvents;
  } catch (error: any) {
    // Check if token expired
    if (error.code === 401 || error.message?.includes('invalid_grant')) {
      // Try to refresh the token
      try {
        const newCredentials = await refreshAccessToken(refreshToken);

        // Update stored tokens
        await storage.updateGoogleToken(userId, {
          accessToken: newCredentials.access_token,
          expiresAt: new Date(newCredentials.expiry_date || Date.now() + 3600000)
        });

        // Retry fetching events with new token
        return getCalendarEvents(newCredentials.access_token!, refreshToken, userId);
      } catch (refreshError) {
        console.error('Error refreshing token:', refreshError);
        throw new Error('Failed to refresh access token');
      }
    }

    console.error('Error fetching calendar events:', error);
    throw error;
  }
}

