import { useState } from 'react';
import { useRoute } from 'wouter';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { Calendar, Clock, CheckCircle, AlertCircle, User, Briefcase, MapPin } from 'lucide-react';

export default function PublicReschedulePage() {
  const [, params] = useRoute('/reschedule/:appointmentId');
  const appointmentId = params?.appointmentId;
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    date: '',
    time: '',
    note: ''
  });

  const [isSuccess, setIsSuccess] = useState(false);

  const { data: appointment, isLoading, error } = useQuery<{
    id: string;
    customerName: string;
    customerEmail: string;
    date: string;
    time: string;
    status: string;
    serviceName: string;
    businessName: string;
    contactEmail?: string;
    contactPhone?: string;
  }>({
    queryKey: [`/api/public/appointments/${appointmentId}`],
    enabled: !!appointmentId,
  });

  const rescheduleMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch(`/api/public/appointments/${appointmentId}/reschedule`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to reschedule');
      }

      return response.json();
    },
    onSuccess: () => {
      setIsSuccess(true);
      toast({
        title: "Appointment Rescheduled!",
        description: "You'll receive a confirmation email shortly.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reschedule appointment",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.date || !formData.time) {
      toast({
        title: "Missing Information",
        description: "Please select both a date and time",
        variant: "destructive",
      });
      return;
    }

    rescheduleMutation.mutate({
      newDate: formData.date,
      newTime: formData.time,
      note: formData.note,
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center p-4">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-gray-200 border-t-blue-600 mx-auto mb-6"></div>
          <p className="text-lg font-medium text-gray-700 dark:text-gray-200">Loading your appointment...</p>
        </div>
      </div>
    );
  }

  if (error || !appointment) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-10 border border-gray-200 dark:border-gray-700">
            <div className="text-center">
              <div className="mx-auto w-20 h-20 bg-red-50 dark:bg-red-900/30 rounded-full flex items-center justify-center mb-6">
                <AlertCircle className="h-10 w-10 text-red-600 dark:text-red-400" />
              </div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-3">Appointment Not Found</h1>
              <p className="text-gray-600 dark:text-gray-300 leading-relaxed">
                This appointment link may be invalid or expired. Please contact the business if you need assistance.
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (isSuccess) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center p-4">
        <div className="max-w-lg w-full">
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-10 border border-gray-200 dark:border-gray-700">
            <div className="text-center">
              <div className="mx-auto w-20 h-20 bg-green-50 dark:bg-green-900/30 rounded-full flex items-center justify-center mb-6">
                <CheckCircle className="h-10 w-10 text-green-600 dark:text-green-400" />
              </div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-3">Successfully Rescheduled!</h1>
              <p className="text-gray-600 dark:text-gray-300 mb-6 leading-relaxed">
                Your appointment has been rescheduled. Both you and the business will receive confirmation emails shortly.
              </p>

              <div className="bg-blue-50 dark:bg-blue-900/20 rounded-xl p-6 border border-blue-200 dark:border-blue-800">
                <h3 className="font-semibold text-lg text-gray-900 dark:text-white mb-4 flex items-center justify-center">
                  <Calendar className="h-5 w-5 mr-2 text-blue-600 dark:text-blue-400" />
                  New Appointment Details
                </h3>
                <div className="space-y-3 text-left">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600 dark:text-gray-300 flex items-center">
                      <Calendar className="h-4 w-4 mr-2" />
                      Date:
                    </span>
                    <span className="font-semibold text-gray-900 dark:text-white">{formData.date}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600 dark:text-gray-300 flex items-center">
                      <Clock className="h-4 w-4 mr-2" />
                      Time:
                    </span>
                    <span className="font-semibold text-gray-900 dark:text-white">{formData.time}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="text-center mt-6">
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Powered by <span className="font-semibold text-blue-600 dark:text-blue-400">BookingGen</span>
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center p-4 py-12">
      <div className="max-w-3xl w-full">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-blue-600 rounded-2xl shadow-lg mb-6">
            <Calendar className="h-10 w-10 text-white" />
          </div>
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-3">Reschedule Your Appointment</h1>
          <p className="text-lg text-gray-600 dark:text-gray-300">Choose a new date and time that works best for you</p>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg border border-gray-200 dark:border-gray-700 overflow-hidden">
          {/* Current Appointment Section */}
          <div className="bg-blue-600 p-8">
            <h2 className="text-xl font-semibold text-white mb-6 flex items-center">
              <AlertCircle className="h-5 w-5 mr-2" />
              Current Appointment Details
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
                <div className="flex items-start">
                  <MapPin className="h-5 w-5 text-blue-100 mt-0.5 mr-3 flex-shrink-0" />
                  <div>
                    <p className="text-xs text-blue-100 uppercase tracking-wide mb-1">Business</p>
                    <p className="text-white font-semibold">{appointment.businessName || 'N/A'}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
                <div className="flex items-start">
                  <Briefcase className="h-5 w-5 text-blue-100 mt-0.5 mr-3 flex-shrink-0" />
                  <div>
                    <p className="text-xs text-blue-100 uppercase tracking-wide mb-1">Service</p>
                    <p className="text-white font-semibold">{appointment.serviceName}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
                <div className="flex items-start">
                  <Calendar className="h-5 w-5 text-blue-100 mt-0.5 mr-3 flex-shrink-0" />
                  <div>
                    <p className="text-xs text-blue-100 uppercase tracking-wide mb-1">Current Date</p>
                    <p className="text-white font-semibold">{appointment.date}</p>
                  </div>
                </div>
              </div>

              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border border-white/20">
                <div className="flex items-start">
                  <Clock className="h-5 w-5 text-blue-100 mt-0.5 mr-3 flex-shrink-0" />
                  <div>
                    <p className="text-xs text-blue-100 uppercase tracking-wide mb-1">Current Time</p>
                    <p className="text-white font-semibold">{appointment.time}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Reschedule Form */}
          <div className="p-8 bg-white dark:bg-gray-800">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">Select New Date & Time</h2>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="date" className="text-gray-900 dark:text-white font-medium flex items-center">
                    <Calendar className="h-4 w-4 mr-2 text-blue-600" />
                    New Date *
                  </Label>
                  <Input
                    id="date"
                    type="date"
                    value={formData.date}
                    onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                    className="h-12 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:border-blue-500 focus:ring-blue-500"
                    min={new Date().toISOString().split('T')[0]}
                    required
                    data-testid="input-reschedule-date"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="time" className="text-gray-900 dark:text-white font-medium flex items-center">
                    <Clock className="h-4 w-4 mr-2 text-blue-600" />
                    New Time *
                  </Label>
                  <Input
                    id="time"
                    type="time"
                    value={formData.time}
                    onChange={(e) => setFormData({ ...formData, time: e.target.value })}
                    className="h-12 bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:border-blue-500 focus:ring-blue-500"
                    required
                    data-testid="input-reschedule-time"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="note" className="text-gray-900 dark:text-white font-medium">
                  Additional Notes (Optional)
                </Label>
                <Textarea
                  id="note"
                  value={formData.note}
                  onChange={(e) => setFormData({ ...formData, note: e.target.value })}
                  placeholder="Any special requests, reasons for rescheduling, or notes for the business..."
                  className="min-h-[120px] bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 focus:border-blue-500 focus:ring-blue-500"
                  data-testid="input-reschedule-note"
                />
              </div>

              <Button
                type="submit"
                className="w-full h-14 bg-blue-600 hover:bg-blue-700 text-white text-lg font-semibold rounded-xl shadow-lg hover:shadow-xl transition-all duration-200"
                disabled={rescheduleMutation.isPending}
                data-testid="button-submit-reschedule"
              >
                {rescheduleMutation.isPending ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-3 border-white/30 border-t-white mr-3"></div>
                    Rescheduling...
                  </div>
                ) : (
                  <div className="flex items-center justify-center">
                    <Calendar className="h-5 w-5 mr-2" />
                    Confirm Reschedule
                  </div>
                )}
              </Button>
            </form>
          </div>
        </div>

        {/* Footer Branding */}
        <div className="text-center mt-8">
          <p className="text-sm text-gray-600 dark:text-gray-400">
            Powered by <span className="font-semibold text-blue-600 dark:text-blue-400">BookingGen</span> - Effortless Appointment Management
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/components/PayPalSubscription.content:
import { useEffect, useRef, useState } from 'react';
import { useAuth } from '@/hooks/use-auth';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { Loader2 } from 'lucide-react';

interface PayPalSubscriptionButtonProps {
  onSuccess?: (subscriptionId: string) => void;
  onError?: (error: any) => void;
}

declare global {
  interface Window {
    paypal?: any;
  }
}

export default function PayPalSubscriptionButton({ 
  onSuccess, 
  onError 
}: PayPalSubscriptionButtonProps) {
  const { user, profile } = useAuth();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [planId, setPlanId] = useState<string | null>(null);
  const paypalRef = useRef<HTMLDivElement>(null);
  const scriptLoadedRef = useRef(false);
  const isMountedRef = useRef(true);
  const buttonRenderedRef = useRef(false);

  useEffect(() => {
    if (!user) return;

    const fetchPlanId = async () => {
      try {
        // Use regular fetch for public endpoint (no auth required)
        const response = await fetch('/api/paypal/plan-id');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();

        if (!data.planId) {
          throw new Error('Plan ID not found');
        }

        setPlanId(data.planId);
      } catch (err) {
        console.error('Failed to fetch plan ID:', err);
        if (isMountedRef.current) {
          setError('Failed to load payment options');
          setIsLoading(false);
          toast({
            title: "Configuration Error",
            description: "Failed to load payment options. Please try again or contact support.",
            variant: "destructive",
          });
        }
      }
    };

    fetchPlanId();
  }, [user]);

  useEffect(() => {
    if (!user || !planId || !paypalRef.current || buttonRenderedRef.current) return;

    const loadPayPalScript = async () => {
      try {
        // Check if PayPal script is already loaded
        if (window.paypal && scriptLoadedRef.current) {
          renderPayPalButton();
          return;
        }

        // Load PayPal SDK
        const script = document.createElement('script');
        const clientId = import.meta.env.VITE_PAYPAL_CLIENT_ID;

        if (!clientId) {
          if (isMountedRef.current) {
            setError('PayPal Client ID not configured');
            setIsLoading(false);
            toast({
              title: "Configuration Error",
              description: "PayPal is not properly configured. Please contact support.",
              variant: "destructive",
            });
          }
          return;
        }

        script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}&vault=true&intent=subscription`;
        script.async = true;

        script.onload = () => {
          scriptLoadedRef.current = true;
          if (isMountedRef.current) {
            renderPayPalButton();
          }
        };

        script.onerror = () => {
          if (isMountedRef.current) {
            setError('Failed to load PayPal SDK');
            setIsLoading(false);
            toast({
              title: "Error",
              description: "Failed to load PayPal payment system",
              variant: "destructive",
            });
          }
        };

        document.body.appendChild(script);
      } catch (err) {
        console.error('Error loading PayPal:', err);
        if (isMountedRef.current) {
          setError('Failed to initialize PayPal');
          setIsLoading(false);
        }
      }
    };

    const renderPayPalButton = () => {
      if (!window.paypal || !paypalRef.current || buttonRenderedRef.current || !planId) return;

      // Mark button as rendered to prevent re-rendering
      buttonRenderedRef.current = true;

      // Capture user info at render time to avoid dependency issues
      const userId = user.id;
      const userEmail = user.email;
      const userName = profile?.fullName || user.fullName || userEmail;

      window.paypal.Buttons({
        style: {
          layout: 'vertical',
          color: 'blue',
          shape: 'rect',
          label: 'subscribe',
          height: 45,
        },
        createSubscription: async (data: any, actions: any) => {
          try {
            console.log('ðŸ”µ Creating PayPal subscription with plan ID:', planId);
            console.log('ðŸ”µ User info:', { userId, userEmail, userName });

            // Create subscription using PayPal SDK
            const subscriptionId = await actions.subscription.create({
              plan_id: planId,
              custom_id: userId, // Store user ID for webhook processing
              subscriber: {
                name: {
                  given_name: userName,
                },
                email_address: userEmail,
              },
            });

            console.log('âœ… PayPal subscription created:', subscriptionId);
            return subscriptionId;
          } catch (error) {
            console.error('âŒ Failed to create PayPal subscription:', error);
            console.error('Error details:', JSON.stringify(error, null, 2));
            throw error;
          }
        },
        onApprove: async (data: any) => {
          try {
            console.log('âœ… PayPal subscription approved:', data.subscriptionID);

            toast({
              title: "Processing...",
              description: "Activating your subscription. Please wait...",
            });

            // Wait a moment for webhook to process
            await new Promise(resolve => setTimeout(resolve, 3000));

            // First try to check if webhook already activated it
            await queryClient.invalidateQueries({ queryKey: ['/api/profile'] });
            let profileResponse = await apiRequest('GET', '/api/profile');
            let updatedProfile = await profileResponse.json();

            if (updatedProfile.membershipStatus === 'pro') {
              console.log('âœ… Subscription activated via webhook');
              toast({
                title: "Success!",
                description: "Your PayPal subscription has been activated. Welcome to Pro!",
              });
            } else {
              // Webhook might be delayed, use fallback to check and activate
              console.log('â³ Webhook delayed, using fallback activation...');

              try {
                const checkResponse = await apiRequest('POST', '/api/paypal/subscription/check-activate', {
                  subscriptionId: data.subscriptionID
                });
                const checkResult = await checkResponse.json();

                if (checkResult.success && checkResult.activated) {
                  console.log('âœ… Subscription activated via fallback');
                  await queryClient.invalidateQueries({ queryKey: ['/api/profile'] });
                  toast({
                    title: "Success!",
                    description: "Your PayPal subscription has been activated. Welcome to Pro!",
                  });
                } else if (checkResult.success && !checkResult.activated) {
                  console.log('âœ… Subscription already active');
                  toast({
                    title: "Success!",
                    description: "Your subscription is active. Welcome to Pro!",
                  });
                } else {
                  console.warn('âš ï¸ Subscription not yet active');
                  toast({
                    title: "Subscription Created",
                    description: "Your subscription is being processed. It may take up to 5 minutes to activate. Please refresh the page shortly.",
                  });
                }
              } catch (fallbackError) {
                console.error('âŒ Fallback activation failed:', fallbackError);
                toast({
                  title: "Subscription Approved",
                  description: "Your subscription is being activated. It may take up to 5 minutes. Please refresh the page shortly.",
                });
              }
            }

            if (onSuccess) {
              onSuccess(data.subscriptionID);
            }

            // Reload to ensure all UI updates
            setTimeout(() => {
              window.location.reload();
            }, 2000);
          } catch (err) {
            console.error('âŒ Approval error:', err);
            toast({
              title: "Subscription Approved",
              description: "Your subscription is being activated. Please refresh the page in a few moments.",
            });
            if (onError) {
              onError(err);
            }
          }
        },
        onCancel: (data: any) => {
          console.log('Subscription cancelled by user:', data);
          toast({
            title: "Cancelled",
            description: "Subscription process was cancelled.",
          });
        },
        onError: (err: any) => {
          console.error('âŒ PayPal button error:', err);

          let errorMessage = "There was an error processing your payment. Please try again.";

          // Provide more specific error messages when possible
          if (err && typeof err === 'object') {
            if (err.message) {
              errorMessage = err.message;
            } else if (err.toString) {
              errorMessage = err.toString();
            }
          }

          toast({
            title: "Payment Error",
            description: errorMessage,
            variant: "destructive",
          });

          if (onError) {
            onError(err);
          }
        },
      }).render(paypalRef.current);

      if (isMountedRef.current) {
        setIsLoading(false);
      }
    };

    loadPayPalScript();

    return () => {
      // Cleanup: mark component as unmounted
      isMountedRef.current = false;
    };
  }, [user, planId]);

  if (error) {
    return (
      <div className="text-center p-4 bg-destructive/10 text-destructive rounded-lg">
        <p>{error}</p>
      </div>
    );
  }

  return (
    <div className="w-full">
      {isLoading && (
        <div className="flex items-center justify-center p-4">
          <Loader2 className="h-6 w-6 animate-spin text-primary" />
          <span className="ml-2 text-sm text-muted-foreground">Loading PayPal...</span>
        </div>
      )}
      <div ref={paypalRef} className="w-full" data-testid="paypal-subscription-button" />
    </div>
  );
}

