import { Request, Response } from "express";
import { storage } from "./storage";
import crypto from "crypto";

const PAYPAL_API_BASE = 
  process.env.NODE_ENV === "production"
    ? "https://api-m.paypal.com"
    : "https://api-m.sandbox.paypal.com";

const PAYPAL_WEBHOOK_ID = process.env.PAYPAL_WEBHOOK_ID || "";

// Get PayPal access token for webhook verification
async function getAccessToken(): Promise<string> {
  const auth = Buffer.from(
    `${process.env.PAYPAL_CLIENT_ID}:${process.env.PAYPAL_CLIENT_SECRET}`
  ).toString("base64");

  const response = await fetch(`${PAYPAL_API_BASE}/v1/oauth2/token`, {
    method: "POST",
    headers: {
      "Authorization": `Basic ${auth}`,
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: "grant_type=client_credentials",
  });

  if (!response.ok) {
    throw new Error(`Failed to get access token: ${response.statusText}`);
  }

  const data = await response.json();
  return data.access_token;
}

// Verify PayPal webhook signature using PayPal API
async function verifyWebhookSignature(
  webhookId: string,
  headers: any,
  body: string
): Promise<boolean> {
  try {
    const accessToken = await getAccessToken();

    const verificationData = {
      transmission_id: headers["paypal-transmission-id"],
      transmission_time: headers["paypal-transmission-time"],
      cert_url: headers["paypal-cert-url"],
      auth_algo: headers["paypal-auth-algo"],
      transmission_sig: headers["paypal-transmission-sig"],
      webhook_id: webhookId,
      webhook_event: JSON.parse(body),
    };

    const response = await fetch(
      `${PAYPAL_API_BASE}/v1/notifications/verify-webhook-signature`,
      {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(verificationData),
      }
    );

    if (!response.ok) {
      console.error("Webhook verification failed:", await response.text());
      return false;
    }

    const result = await response.json();
    return result.verification_status === "SUCCESS";
  } catch (error) {
    console.error("Webhook verification error:", error);
    return false;
  }
}

// PayPal Webhook Event Handler
export async function handlePayPalWebhook(req: Request, res: Response) {
  try {
    // Parse the raw body
    const rawBody = req.body.toString('utf8');
    const event = JSON.parse(rawBody);

    console.log("Received PayPal webhook event:", event.event_type);

    // SECURITY: Require webhook ID for signature verification
    // Only allow unverified webhooks in development mode with explicit flag
    const allowUnverifiedWebhooks = process.env.NODE_ENV === 'development' && process.env.PAYPAL_ALLOW_UNVERIFIED_WEBHOOKS === 'true';

    if (!PAYPAL_WEBHOOK_ID && !allowUnverifiedWebhooks) {
      console.error("âŒ PAYPAL_WEBHOOK_ID not configured and unverified webhooks are not allowed");
      console.error("âŒ Set PAYPAL_WEBHOOK_ID for production or PAYPAL_ALLOW_UNVERIFIED_WEBHOOKS=true for development");
      return res.status(500).json({ error: "Webhook verification not configured" });
    }

    // Verify webhook signature
    if (PAYPAL_WEBHOOK_ID) {
      const isValid = await verifyWebhookSignature(
        PAYPAL_WEBHOOK_ID,
        req.headers,
        rawBody
      );

      if (!isValid) {
        console.error("Invalid PayPal webhook signature");
        return res.status(401).json({ error: "Invalid signature" });
      }

      console.log("PayPal webhook signature verified successfully");
    } else {
      console.warn("âš ï¸ DEVELOPMENT MODE: Processing webhook without signature verification");
      console.warn("âš ï¸ This is ONLY safe in development. DO NOT use in production!");
    }

    // Handle different event types
    switch (event.event_type) {
      case "BILLING.SUBSCRIPTION.ACTIVATED":
        await handleSubscriptionActivated(event);
        break;

      case "PAYMENT.SALE.COMPLETED":
        await handlePaymentCompleted(event);
        break;

      case "BILLING.SUBSCRIPTION.CANCELLED":
        await handleSubscriptionCancelled(event);
        break;

      case "BILLING.SUBSCRIPTION.SUSPENDED":
        await handleSubscriptionSuspended(event);
        break;

      case "BILLING.SUBSCRIPTION.EXPIRED":
        await handleSubscriptionExpired(event);
        break;

      default:
        console.log(`Unhandled event type: ${event.event_type}`);
    }

    // Always respond with 200 OK to acknowledge receipt
    res.status(200).json({ received: true });
  } catch (error) {
    console.error("PayPal webhook error:", error);
    res.status(500).json({ error: "Webhook processing failed" });
  }
}

// Handle subscription activated event
async function handleSubscriptionActivated(event: any) {
  try {
    const subscription = event.resource;
    const subscriptionId = subscription.id;
    const userId = subscription.custom_id; // We store userId in custom_id when creating subscription
    const planId = subscription.plan_id;
    const status = subscription.status;

    console.log("Subscription activated:", {
      subscriptionId,
      userId,
      status,
    });

    if (!userId) {
      console.error("No userId found in subscription custom_id");
      return;
    }

    // Calculate subscription period
    const currentDate = new Date();
    const nextBillingDate = subscription.billing_info?.next_billing_time 
      ? new Date(subscription.billing_info.next_billing_time)
      : new Date(currentDate.getTime() + 30 * 24 * 60 * 60 * 1000); // 30 days from now

    // Update user profile to Pro
    await storage.updateProfile(userId, {
      membershipStatus: "pro",
      membershipPlan: "pro-monthly",
      membershipExpires: nextBillingDate,
    });

    // Store subscription details in database
    await storage.createSubscription({
      id: subscriptionId,
      userId: userId,
      status: status,
      planId: planId,
      currentPeriodStart: currentDate,
      currentPeriodEnd: nextBillingDate,
      planName: "pro",
      currency: "USD",
      amount: "14.99",
      startTime: currentDate,
      nextBillingTime: nextBillingDate,
      isTrial: false,
    });

    console.log(`User ${userId} upgraded to Pro via PayPal subscription ${subscriptionId}`);
  } catch (error) {
    console.error("Error handling subscription activation:", error);
    throw error;
  }
}

// Handle payment completed event (recurring payments)
async function handlePaymentCompleted(event: any) {
  try {
    const sale = event.resource;
    const subscriptionId = sale.billing_agreement_id;

    if (!subscriptionId) {
      console.log("Payment not related to subscription, skipping");
      return;
    }

    console.log("Recurring payment completed:", {
      subscriptionId,
      amount: sale.amount?.total,
      currency: sale.amount?.currency,
    });

    // Get subscription from database
    const subscription = await storage.getSubscription(subscriptionId);

    if (!subscription) {
      console.error("Subscription not found:", subscriptionId);
      return;
    }

    // Update subscription billing dates
    const currentDate = new Date();
    const nextBillingDate = new Date(currentDate.getTime() + 30 * 24 * 60 * 60 * 1000); // 30 days from now

    await storage.updateSubscription(subscriptionId, {
      currentPeriodStart: currentDate,
      currentPeriodEnd: nextBillingDate,
      nextBillingTime: nextBillingDate,
      status: "ACTIVE",
    });

    // Ensure user profile is still Pro
    await storage.updateProfile(subscription.userId, {
      membershipStatus: "pro",
      membershipExpires: nextBillingDate,
    });

    console.log(`Subscription ${subscriptionId} renewed successfully`);
  } catch (error) {
    console.error("Error handling payment completion:", error);
    throw error;
  }
}

// Handle subscription cancelled event
async function handleSubscriptionCancelled(event: any) {
  try {
    const subscription = event.resource;
    const subscriptionId = subscription.id;

    console.log("Subscription cancelled:", subscriptionId);

    // Get subscription from database
    const dbSubscription = await storage.getSubscription(subscriptionId);

    if (!dbSubscription) {
      console.error("Subscription not found:", subscriptionId);
      return;
    }

    // Update subscription status
    await storage.updateSubscription(subscriptionId, {
      status: "CANCELLED",
    });

    // Downgrade user to free at the end of the current billing period
    // Don't immediately revoke access - let them use it until period ends
    console.log(`Subscription ${subscriptionId} marked as cancelled. Access will end on ${dbSubscription.currentPeriodEnd}`);
  } catch (error) {
    console.error("Error handling subscription cancellation:", error);
    throw error;
  }
}

// Handle subscription suspended event
async function handleSubscriptionSuspended(event: any) {
  try {
    const subscription = event.resource;
    const subscriptionId = subscription.id;

    console.log("Subscription suspended:", subscriptionId);

    // Get subscription from database
    const dbSubscription = await storage.getSubscription(subscriptionId);

    if (!dbSubscription) {
      console.error("Subscription not found:", subscriptionId);
      return;
    }

    // Update subscription status
    await storage.updateSubscription(subscriptionId, {
      status: "SUSPENDED",
    });

    // Downgrade user to free immediately
    await storage.updateProfile(dbSubscription.userId, {
      membershipStatus: "free",
      membershipPlan: null,
      membershipExpires: null,
    });

    console.log(`User ${dbSubscription.userId} downgraded to free due to suspended subscription`);
  } catch (error) {
    console.error("Error handling subscription suspension:", error);
    throw error;
  }
}

// Handle subscription expired event
async function handleSubscriptionExpired(event: any) {
  try {
    const subscription = event.resource;
    const subscriptionId = subscription.id;

    console.log("Subscription expired:", subscriptionId);

    // Get subscription from database
    const dbSubscription = await storage.getSubscription(subscriptionId);

    if (!dbSubscription) {
      console.error("Subscription not found:", subscriptionId);
      return;
    }

    // Update subscription status
    await storage.updateSubscription(subscriptionId, {
      status: "EXPIRED",
    });

    // Downgrade user to free
    await storage.updateProfile(dbSubscription.userId, {
      membershipStatus: "free",
      membershipPlan: null,
      membershipExpires: null,
    });

    console.log(`User ${dbSubscription.userId} downgraded to free due to expired subscription`);
  } catch (error) {
    console.error("Error handling subscription expiration:", error);
    throw error;
  }
}

server/paypal-subscriptions.ts.content:
import { Request, Response } from "express";
import crypto from "crypto";

const { PAYPAL_CLIENT_ID, PAYPAL_CLIENT_SECRET } = process.env;

if (!PAYPAL_CLIENT_ID) {
  throw new Error("Missing PAYPAL_CLIENT_ID");
}
if (!PAYPAL_CLIENT_SECRET) {
  throw new Error("Missing PAYPAL_CLIENT_SECRET");
}

// PayPal API base URLs
const PAYPAL_API_BASE = 
  process.env.NODE_ENV === "production"
    ? "https://api-m.paypal.com"
    : "https://api-m.sandbox.paypal.com";

// Store plan IDs in memory - will search for existing plans on startup
let PRO_PLAN_ID: string | null = null;
const PRO_PLAN_NAME = "BookingGen Pro Monthly";

// Get PayPal access token
async function getAccessToken(): Promise<string> {
  const auth = Buffer.from(`${PAYPAL_CLIENT_ID}:${PAYPAL_CLIENT_SECRET}`).toString("base64");

  const response = await fetch(`${PAYPAL_API_BASE}/v1/oauth2/token`, {
    method: "POST",
    headers: {
      "Authorization": `Basic ${auth}`,
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: "grant_type=client_credentials",
  });

  if (!response.ok) {
    throw new Error(`Failed to get access token: ${response.statusText}`);
  }

  const data = await response.json();
  return data.access_token;
}

// Create a product
async function createProduct(accessToken: string): Promise<string> {
  const response = await fetch(`${PAYPAL_API_BASE}/v1/catalogs/products`, {
    method: "POST",
    headers: {
      "Authorization": `Bearer ${accessToken}`,
      "Content-Type": "application/json",
      "PayPal-Request-Id": `PRODUCT-${Date.now()}`,
    },
    body: JSON.stringify({
      name: "BookingGen Pro",
      description: "Professional booking page solution with unlimited features",
      type: "SERVICE",
      category: "SOFTWARE",
    }),
  });

  if (!response.ok) {
    const error = await response.text();
    throw new Error(`Failed to create product: ${error}`);
  }

  const data = await response.json();
  console.log("Created PayPal product:", data.id);
  return data.id;
}

// Create a subscription plan
async function createPlan(accessToken: string, productId: string): Promise<string> {
  const response = await fetch(`${PAYPAL_API_BASE}/v1/billing/plans`, {
    method: "POST",
    headers: {
      "Authorization": `Bearer ${accessToken}`,
      "Content-Type": "application/json",
      "PayPal-Request-Id": `PLAN-${Date.now()}`,
    },
    body: JSON.stringify({
      product_id: productId,
      name: "BookingGen Pro Monthly",
      description: "Monthly subscription for BookingGen Pro with all features unlocked",
      billing_cycles: [
        {
          frequency: {
            interval_unit: "MONTH",
            interval_count: 1,
          },
          tenure_type: "REGULAR",
          sequence: 1,
          total_cycles: 0, // 0 means infinite
          pricing_scheme: {
            fixed_price: {
              value: "14.99",
              currency_code: "USD",
            },
          },
        },
      ],
      payment_preferences: {
        auto_bill_outstanding: true,
        setup_fee: {
          value: "0",
          currency_code: "USD",
        },
        setup_fee_failure_action: "CONTINUE",
        payment_failure_threshold: 3,
      },
    }),
  });

  if (!response.ok) {
    const error = await response.text();
    throw new Error(`Failed to create plan: ${error}`);
  }

  const data = await response.json();
  console.log("Created PayPal billing plan:", data.id);
  return data.id;
}

// Search for existing plan by name
async function findExistingPlan(accessToken: string): Promise<string | null> {
  try {
    const response = await fetch(`${PAYPAL_API_BASE}/v1/billing/plans?page_size=20&total_required=true`, {
      method: "GET",
      headers: {
        "Authorization": `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
    });

    if (!response.ok) {
      console.error("Failed to search plans:", await response.text());
      return null;
    }

    const data = await response.json();
    const existingPlan = data.plans?.find((plan: any) => plan.name === PRO_PLAN_NAME);

    if (existingPlan) {
      console.log(`Found existing PayPal plan: ${existingPlan.id}`);
      return existingPlan.id;
    }

    return null;
  } catch (error) {
    console.error("Error searching for existing plans:", error);
    return null;
  }
}

// Get or create Pro subscription plan
export async function getOrCreateProPlan(): Promise<string> {
  if (PRO_PLAN_ID) {
    return PRO_PLAN_ID;
  }

  try {
    const accessToken = await getAccessToken();

    // First, try to find existing plan
    const existingPlanId = await findExistingPlan(accessToken);
    if (existingPlanId) {
      PRO_PLAN_ID = existingPlanId;
      console.log(`Using existing PayPal Pro plan: ${existingPlanId}`);
      return existingPlanId;
    }

    console.log("No existing plan found, creating new PayPal Pro plan...");

    // Create product first
    const productId = await createProduct(accessToken);

    // Then create billing plan
    const planId = await createPlan(accessToken, productId);

    PRO_PLAN_ID = planId;
    console.log(`âœ… PayPal Pro Plan created successfully: ${planId}`);
    return planId;
  } catch (error) {
    console.error("Failed to create PayPal plan:", error);
    throw error;
  }
}

// Get PayPal plan ID (creates plan if it doesn't exist)
export async function getPayPalPlanId(req: Request, res: Response) {
  try {
    const planId = await getOrCreateProPlan();
    res.json({ planId });
  } catch (error: any) {
    console.error("âŒ Failed to get PayPal plan ID:", error);
    res.status(500).json({ 
      error: "Failed to get plan ID",
      details: error.message || "Unknown error occurred"
    });
  }
}

// Create a subscription
export async function createPayPalSubscription(req: Request, res: Response) {
  try {
    const { userId, userEmail, userName } = req.body;

    if (!userId || !userEmail) {
      return res.status(400).json({ 
        error: "Missing required fields",
        details: "userId and userEmail are required" 
      });
    }

    console.log(`Creating PayPal subscription for user ${userId} (${userEmail})`);

    const accessToken = await getAccessToken();
    const planId = await getOrCreateProPlan();

    const baseUrl = process.env.NODE_ENV === 'production' 
      ? 'https://www.bookinggen.xyz' 
      : 'http://localhost:5000';

    const response = await fetch(`${PAYPAL_API_BASE}/v1/billing/subscriptions`, {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${accessToken}`,
        "Content-Type": "application/json",
        "PayPal-Request-Id": `SUB-${Date.now()}`,
      },
      body: JSON.stringify({
        plan_id: planId,
        subscriber: {
          name: {
            given_name: userName || "User",
          },
          email_address: userEmail,
        },
        application_context: {
          brand_name: "BookingGen",
          locale: "en-US",
          shipping_preference: "NO_SHIPPING",
          user_action: "SUBSCRIBE_NOW",
          payment_method: {
            payer_selected: "PAYPAL",
            payee_preferred: "IMMEDIATE_PAYMENT_REQUIRED",
          },
          return_url: `${baseUrl}/dashboard?paypal_success=true`,
          cancel_url: `${baseUrl}/dashboard?paypal_canceled=true`,
        },
        custom_id: userId, // Store userId for webhook processing
      }),
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Failed to create subscription: ${error}`);
    }

    const data = await response.json();
    console.log("âœ… Created PayPal subscription:", data.id);

    const approvalUrl = data.links?.find((link: any) => link.rel === "approve")?.href;

    if (!approvalUrl) {
      throw new Error("No approval URL returned from PayPal");
    }

    res.json({
      subscriptionId: data.id,
      approvalUrl: approvalUrl,
      status: data.status,
    });
  } catch (error: any) {
    console.error("âŒ Failed to create PayPal subscription:", error);
    res.status(500).json({ 
      error: "Failed to create subscription",
      details: error.message || "Unknown error occurred"
    });
  }
}

// Get subscription details
